import React, { useState, useEffect, useMemo, useRef } from 'react';
import { LineChart, Line, BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, PieChart, Pie, Cell, ScatterChart, Scatter } from 'recharts';
import { Car, Calendar, TrendingUp, DollarSign, Activity, Settings, Download, Upload, Info, X, ExternalLink, AlertCircle, ChevronDown, ChevronUp, Gauge, Wrench, History, Package, ArrowUpDown, Filter, RotateCcw, Eye, EyeOff, Check } from 'lucide-react';
import Papa from 'papaparse';

// グレード名統一設定
const CAR_GRADES_CONFIG = {
  "RC F": {
    grades: [
      "RC F",
      "RC F Carbon Exterior Package",
      "RC F Performance Package",
      "RC F Track Edition",
      "RC F 10th Anniversary",
      "ベース"
    ],
    special_patterns: {
      "カーボンエクステリアパッケージ": "RC F Carbon Exterior Package",
      "パフォーマンスパッケージ": "RC F Performance Package",
      "トラックエディション": "RC F Track Edition",
      "10周年": "RC F 10th Anniversary"
    }
  }
};

// グレード名を統一する関数（動的設定対応）
const normalizeGrade = (grade, carType, gradesConfig) => {
  if (!grade || !carType || !gradesConfig) return grade;
  
  // car_grades.jsonから該当車種の設定を探す
  let config = null;
  
  // 車種名で直接マッチを試す
  if (gradesConfig[carType]) {
    config = gradesConfig[carType];
  } else {
    // aliasesでマッチを試す
    for (const [key, value] of Object.entries(gradesConfig)) {
      if (value.aliases && value.aliases.includes(carType)) {
        config = value;
        break;
      }
    }
  }
  
  if (!config) return grade;
  
  // 完全一致チェック
  if (config.grades && config.grades.includes(grade)) {
    return grade;
  }
  
  // 特殊パターンチェック
  if (config.special_patterns) {
    for (const [pattern, normalized] of Object.entries(config.special_patterns)) {
      if (grade.includes(pattern)) {
        return normalized;
      }
    }
  }
  
  // 部分一致チェック
  if (config.grades) {
    for (const normalizedGrade of config.grades) {
      if (grade.includes(normalizedGrade) || normalizedGrade.includes(grade)) {
        return normalizedGrade;
      }
    }
  }
  
  // マッチしない場合は元のグレード名を返す
  return grade;
};

// データ処理関数
const parsePrice = (priceStr) => {
  if (!priceStr) return null;
  const cleaned = priceStr.toString().replace(/[万円,]/g, '');
  const price = parseFloat(cleaned);
  return isNaN(price) ? null : price;
};

const parseYear = (yearStr) => {
  if (!yearStr) return null;
  const match = yearStr.toString().match(/\d{4}/);
  return match ? parseInt(match[0]) : null;
};

const parseMileage = (mileageStr) => {
  if (!mileageStr) return null;
  const cleaned = mileageStr.toString().replace(/[kmKM,]/g, '');
  const mileage = parseFloat(cleaned);
  if (isNaN(mileage)) return null;
  return mileageStr.toString().includes('万') ? mileage * 10000 : mileage;
};

const categorizeTransmission = (transmission) => {
  if (!transmission) return 'その他';
  const upper = transmission.toUpperCase();
  if (upper.includes('AT')) return 'AT';
  if (upper.includes('CVT')) return 'CVT';
  if (upper.includes('MT')) return 'MT';
  return 'その他';
};

const processCarData = (data, carType, gradesConfig) => {
  return data.map(item => {
    const price = parsePrice(item['価格']);
    const year = parseYear(item['年式']);
    const mileage = parseMileage(item['走行距離']);
    
    // グレード名の統一
    const originalGrade = item['グレード'] || 'Unknown';
    const normalizedGrade = normalizeGrade(originalGrade, carType, gradesConfig);
    
    // スクレイピング取得日の処理
    let scrapeDate = null;
    if (item['スクレイピング取得日']) {
      const dateStr = item['スクレイピング取得日'];
      const dateParts = dateStr.match(/(\d{4})年(\d{1,2})月(\d{1,2})日/);
      if (dateParts) {
        scrapeDate = new Date(
          parseInt(dateParts[1]),
          parseInt(dateParts[2]) - 1,
          parseInt(dateParts[3])
        );
      }
    }
    
    return {
      ...item,
      price,
      year,
      mileage,
      scrapeDate,
      元グレード: originalGrade,
      正規グレード: normalizedGrade,
      グレード: normalizedGrade, // デフォルトは正規グレード
      修復歴: item['修復歴'] || 'Unknown',
      ミッション: item['ミッション'] || 'Unknown',
      url: item['URL'] || '#'
    };
  }).filter(item => item.price !== null && item.year !== null);
};

// データ品質チェック関数
const checkDataQuality = (data) => {
  if (!data || data.length === 0) return { score: 0, issues: ['No data available'] };
  
  const issues = [];
  let validCount = 0;
  const requiredFields = ['価格', '年式', '走行距離', 'グレード', '修復歴'];
  
  data.forEach(item => {
    let hasAllFields = true;
    requiredFields.forEach(field => {
      if (!item[field] || item[field] === 'Unknown') {
        hasAllFields = false;
      }
    });
    if (hasAllFields) validCount++;
  });
  
  const score = Math.round((validCount / data.length) * 100);
  
  if (score < 50) issues.push('Many missing values');
  if (score < 80) issues.push('Some data quality issues');
  
  return { score, issues };
};

// 価格統計計算関数
const calculatePriceStats = (prices) => {
  if (!prices || prices.length === 0) {
    return { mean: 0, min: 0, max: 0, median: 0, count: 0 };
  }
  
  const sorted = [...prices].sort((a, b) => a - b);
  const sum = sorted.reduce((a, b) => a + b, 0);
  
  return {
    mean: Math.round(sum / sorted.length),
    min: sorted[0],
    max: sorted[sorted.length - 1],
    median: sorted[Math.floor(sorted.length / 2)],
    count: sorted.length
  };
};

// CSVエクスポート関数
const exportToCSV = (data, filename) => {
  const headers = ['価格', '年式', '走行距離', 'グレード', '元グレード', '修復歴', 'ミッション', 'URL'];
  const csvContent = [
    headers.join(','),
    ...data.map(row => 
      headers.map(header => {
        let value = row[header];
        if (header === '価格' && row.price) value = `${row.price}万円`;
        if (header === '走行距離' && row.mileage) value = `${row.mileage}km`;
        if (typeof value === 'string' && value.includes(',')) {
          value = `"${value}"`;
        }
        return value || '';
      }).join(',')
    )
  ].join('\n');
  
  const blob = new Blob(['\ufeff' + csvContent], { type: 'text/csv;charset=utf-8;' });
  const link = document.createElement('a');
  link.href = URL.createObjectURL(blob);
  link.download = filename;
  link.click();
};

// 統合レンジスライダーコンポーネント
const RangeSlider = ({ min, max, value, onChange, step = 1, label, unit = '' }) => {
  const [localValue, setLocalValue] = useState(value);
  const [isDragging, setIsDragging] = useState(false);
  
  useEffect(() => {
    setLocalValue(value);
  }, [value]);
  
  const handleMouseDown = (index) => {
    setIsDragging(index);
  };
  
  const handleMouseUp = () => {
    setIsDragging(false);
  };
  
  const handleMouseMove = (e) => {
    if (isDragging === false) return;
    
    const rect = e.currentTarget.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const percentage = Math.max(0, Math.min(1, x / rect.width));
    const newValue = min + (max - min) * percentage;
    
    const newRange = [...localValue];
    newRange[isDragging] = Math.round(newValue / step) * step;
    
    if (isDragging === 0 && newRange[0] > newRange[1]) {
      newRange[0] = newRange[1];
    } else if (isDragging === 1 && newRange[1] < newRange[0]) {
      newRange[1] = newRange[0];
    }
    
    setLocalValue(newRange);
    onChange(newRange);
  };
  
  useEffect(() => {
    if (isDragging !== false) {
      document.addEventListener('mouseup', handleMouseUp);
      return () => document.removeEventListener('mouseup', handleMouseUp);
    }
  }, [isDragging]);
  
  const leftPercentage = ((localValue[0] - min) / (max - min)) * 100;
  const rightPercentage = ((localValue[1] - min) / (max - min)) * 100;
  
  return (
    <div className="w-full">
      <div className="flex justify-between items-center mb-2">
        <span className="text-sm font-medium text-gray-700">{label}</span>
        <span className="text-sm text-gray-600">
          {localValue[0]}{unit} - {localValue[1]}{unit}
        </span>
      </div>
      <div 
        className="relative h-6 bg-gray-200 rounded-full cursor-pointer"
        onMouseMove={handleMouseMove}
      >
        <div 
          className="absolute h-full bg-blue-500 rounded-full"
          style={{
            left: `${leftPercentage}%`,
            width: `${rightPercentage - leftPercentage}%`
          }}
        />
        <div
          className="absolute w-6 h-6 bg-white border-2 border-blue-500 rounded-full shadow-lg cursor-grab active:cursor-grabbing transform -translate-x-1/2"
          style={{ left: `${leftPercentage}%` }}
          onMouseDown={() => handleMouseDown(0)}
        />
        <div
          className="absolute w-6 h-6 bg-white border-2 border-blue-500 rounded-full shadow-lg cursor-grab active:cursor-grabbing transform -translate-x-1/2"
          style={{ left: `${rightPercentage}%` }}
          onMouseDown={() => handleMouseDown(1)}
        />
      </div>
    </div>
  );
};

const CarAnalysisDashboard = () => {
  const [rawData, setRawData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [viewMode, setViewMode] = useState('overview');
  const [overviewMode, setOverviewMode] = useState('all');
  const [timeScale, setTimeScale] = useState('monthly');
  const [trendDateMode, setTrendDateMode] = useState('scraping');
  const [selectedGrades, setSelectedGrades] = useState([]);
  const [yearRange, setYearRange] = useState({ min: 2014, max: 2025 });
  const [mileageMax, setMileageMax] = useState(200000);
  const [priceRange, setPriceRange] = useState([0, 2000]);
  const [transmissionFilters, setTransmissionFilters] = useState({
    AT: true,
    CVT: true,
    MT: true,
    other: true
  });
  const [repairHistoryFilter, setRepairHistoryFilter] = useState('all');
  const [showNormalizedGrades, setShowNormalizedGrades] = useState(true);
  const [fileUploaded, setFileUploaded] = useState(false);
  const [dataQuality, setDataQuality] = useState(null);
  const [lastUpdate, setLastUpdate] = useState(null);
  const [selectedCarDetail, setSelectedCarDetail] = useState(null);
  const [showCarDetailModal, setShowCarDetailModal] = useState(false);
  const [selectedCarType, setSelectedCarType] = useState('');
  const [selectedFile, setSelectedFile] = useState('');
  const [availableCarTypes, setAvailableCarTypes] = useState(['F']);
  const [availableFiles, setAvailableFiles] = useState([]);
  const [carTypeLoading, setCarTypeLoading] = useState(false);
  const [carGradesConfig, setCarGradesConfig] = useState({});
  const fileInputRef = useRef(null);

  // 利用可能な車種を読み込む関数
  const loadAvailableCarTypes = async () => {
    try {
      setCarTypeLoading(true);
      const carTypes = ['F'];
      setAvailableCarTypes(carTypes);
      
      if (carTypes.length > 0) {
        setSelectedCarType(carTypes[0]);
      }
    } catch (error) {
      console.error('車種読み込みエラー:', error);
    } finally {
      setCarTypeLoading(false);
    }
  };

  // 利用可能なファイルを読み込む関数
  const loadAvailableFiles = async (carType) => {
    try {
      if (!carType) return;
      
      const files = [
        {
          path: 'rc_f_data.json',
          displayName: '2025年06月18日データ (79台)',
          date: '2025-06-18'
        }
      ];
      
      setAvailableFiles(files);
      if (files.length > 0) {
        setSelectedFile(files[0].path);
      }
    } catch (error) {
      console.error('ファイル読み込みエラー:', error);
    }
  };

  // 選択されたファイルを読み込む関数
  const loadSelectedFile = async (filePath) => {
    try {
      if (!filePath) return;
      
      setLoading(true);
      
      const response = await fetch(`/data/${filePath}`);
      if (response.ok) {
        const jsonData = await response.json();
        console.log('JSON実データ読み込み:', jsonData.length, '件');
        
        const processedData = processCarData(jsonData, selectedCarType, carGradesConfig);
        setRawData(processedData);
        setFileUploaded(true);
        setLastUpdate(new Date().toLocaleString());
        
        const quality = checkDataQuality(processedData);
        setDataQuality(quality);
        
        const uniqueGrades = [...new Set(processedData.map(item => 
          showNormalizedGrades ? item.正規グレード : item.元グレード
        ))].filter(Boolean);
        setSelectedGrades(uniqueGrades);
        
        const prices = processedData.map(item => item.price).filter(Boolean);
        if (prices.length > 0) {
          const minPrice = Math.floor(Math.min(...prices) / 100) * 100;
          const maxPrice = Math.ceil(Math.max(...prices) / 100) * 100;
          setPriceRange([minPrice, maxPrice]);
        }
        
        setLoading(false);
      } else {
        console.error(`ファイルが見つかりません: ${filePath}`);
        setLoading(false);
      }
    } catch (error) {
      console.error('ファイル読み込みエラー:', error);
      setLoading(false);
    }
  };

  // 初期化処理
  useEffect(() => {
    // car_grades.jsonを読み込む
    fetch('/data/config/car_grades.json')
      .then(res => res.json())
      .then(data => {
        console.log('Car grades config loaded');
        setCarGradesConfig(data);
      })
      .catch(err => {
        console.log('Using default car grades config');
        setCarGradesConfig(CAR_GRADES_CONFIG);
      });
    
    loadAvailableCarTypes();
  }, []);

  // 車種選択時の処理
  useEffect(() => {
    if (selectedCarType) {
      loadAvailableFiles(selectedCarType);
    }
  }, [selectedCarType]);

  // ファイル選択時の処理
  useEffect(() => {
    if (selectedFile) {
      loadSelectedFile(selectedFile);
    }
  }, [selectedFile, carGradesConfig]);

  // データソース別処理
  const processedData = useMemo(() => {
    if (overviewMode === 'latest') {
      const latestData = rawData.filter((item, index, self) => {
        return index === self.findIndex(t => 
          t.price === item.price && 
          t.year === item.year && 
          t.mileage === item.mileage && 
          t.正規グレード === item.正規グレード
        );
      });
      return latestData.slice(-50);
    } else {
      return rawData;
    }
  }, [rawData, overviewMode]);

  // フィルター適用後のデータ
  const filteredData = useMemo(() => {
    return processedData.filter(item => {
      const targetGrade = showNormalizedGrades ? item.正規グレード : item.元グレード;
      
      if (selectedGrades.length > 0 && !selectedGrades.includes(targetGrade)) return false;
      if (item.year < yearRange.min || item.year > yearRange.max) return false;
      if (item.price < priceRange[0] || item.price > priceRange[1]) return false;
      if (item.mileage > mileageMax) return false;
      
      const transmissionCategory = categorizeTransmission(item.ミッション);
      if (!transmissionFilters[transmissionCategory]) return false;
      
      if (repairHistoryFilter !== 'all') {
        if (repairHistoryFilter === 'none' && item.修復歴 !== 'なし') return false;
        if (repairHistoryFilter === 'exists' && item.修復歴 !== 'あり') return false;
      }
      
      return true;
    });
  }, [processedData, selectedGrades, yearRange, priceRange, mileageMax, transmissionFilters, repairHistoryFilter, showNormalizedGrades]);

  // 価格推移データの生成
  const trendData = useMemo(() => {
    if (filteredData.length === 0) return [];
    
    const grouped = {};
    filteredData.forEach(item => {
      let key;
      
      if (trendDateMode === 'year') {
        key = item.year.toString();
      } else if (item.scrapeDate) {
        if (timeScale === 'daily') {
          key = item.scrapeDate.toISOString().split('T')[0];
        } else if (timeScale === 'monthly') {
          key = `${item.scrapeDate.getFullYear()}-${String(item.scrapeDate.getMonth() + 1).padStart(2, '0')}`;
        } else {
          key = item.scrapeDate.getFullYear().toString();
        }
      }
      
      if (key && !grouped[key]) {
        grouped[key] = [];
      }
      if (key) {
        grouped[key].push(item);
      }
    });

    return Object.entries(grouped)
      .map(([key, items]) => {
        const stats = calculatePriceStats(items.map(item => item.price));
        return {
          date: key,
          avgPrice: stats.mean,
          count: items.length,
          maxPrice: stats.max,
          minPrice: stats.min,
          median: stats.median
        };
      })
      .sort((a, b) => a.date.localeCompare(b.date));
  }, [filteredData, timeScale, trendDateMode]);

  // グレード別分析データ
  const gradeAnalysisData = useMemo(() => {
    if (filteredData.length === 0) return [];
    
    const gradeStats = {};
    filteredData.forEach(item => {
      const grade = showNormalizedGrades ? item.正規グレード : item.元グレード;
      if (!gradeStats[grade]) {
        gradeStats[grade] = {
          prices: [],
          years: [],
          mileages: []
        };
      }
      gradeStats[grade].prices.push(item.price);
      gradeStats[grade].years.push(item.year);
      gradeStats[grade].mileages.push(item.mileage);
    });

    return Object.entries(gradeStats)
      .map(([grade, stats]) => {
        const priceStats = calculatePriceStats(stats.prices);
        return {
          grade,
          fullGrade: grade,
          avgPrice: priceStats.mean,
          minPrice: priceStats.min,
          maxPrice: priceStats.max,
          median: priceStats.median,
          count: priceStats.count,
          avgYear: Math.round(stats.years.reduce((sum, year) => sum + year, 0) / stats.years.length),
          avgMileage: Math.round(stats.mileages.reduce((sum, mileage) => sum + mileage, 0) / stats.mileages.length)
        };
      })
      .sort((a, b) => b.avgPrice - a.avgPrice);
  }, [filteredData, showNormalizedGrades]);

  // 年式分布データ
  const yearDistributionData = useMemo(() => {
    const yearCounts = {};
    filteredData.forEach(item => {
      yearCounts[item.year] = (yearCounts[item.year] || 0) + 1;
    });
    
    return Object.entries(yearCounts)
      .map(([year, count]) => ({ year: parseInt(year), count }))
      .sort((a, b) => a.year - b.year);
  }, [filteredData]);

  // 修復歴統計
  const repairHistoryData = useMemo(() => {
    const stats = { あり: 0, なし: 0 };
    filteredData.forEach(item => {
      if (item.修復歴 === 'あり' || item.修復歴 === 'なし') {
        stats[item.修復歴]++;
      }
    });
    
    return [
      { name: '修復歴なし', value: stats.なし, fill: '#10b981' },
      { name: '修復歴あり', value: stats.あり, fill: '#ef4444' }
    ];
  }, [filteredData]);

  // 利用可能なグレード一覧
  const availableGrades = useMemo(() => {
    return [...new Set(processedData.map(item => 
      showNormalizedGrades ? item.正規グレード : item.元グレード
    ))].filter(Boolean).sort();
  }, [processedData, showNormalizedGrades]);

  // グレード選択切り替え
  const handleGradeToggle = (grade) => {
    setSelectedGrades(prev => 
      prev.includes(grade) 
        ? prev.filter(g => g !== grade)
        : [...prev, grade]
    );
  };

  // 全選択/全解除
  const handleSelectAllGrades = () => {
    setSelectedGrades(selectedGrades.length === availableGrades.length ? [] : availableGrades);
  };

  // グレード表示切り替え
  const handleGradeTypeChange = (useNormalized) => {
    setShowNormalizedGrades(useNormalized);
    const uniqueGrades = [...new Set(processedData.map(item => 
      useNormalized ? item.正規グレード : item.元グレード
    ))].filter(Boolean);
    setSelectedGrades(uniqueGrades);
  };

  // ミッション切り替え
  const handleTransmissionToggle = (category) => {
    setTransmissionFilters(prev => ({
      ...prev,
      [category]: !prev[category]
    }));
  };

  // 車両詳細クリック処理
  const handleCarClick = (carData) => {
    setSelectedCarDetail(carData);
    setShowCarDetailModal(true);
  };

  // データエクスポート
  const handleExportData = () => {
    exportToCSV(filteredData, `rc_f_analysis_${new Date().toISOString().split('T')[0]}.csv`);
  };

  // CSVファイルアップロード処理
  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (!file) return;

    Papa.parse(file, {
      header: true,
      encoding: 'UTF-8',
      complete: (results) => {
        console.log('アップロードされたCSV:', results.data);
        const processedData = processCarData(results.data, selectedCarType, carGradesConfig);
        setRawData(processedData);
        setFileUploaded(true);
        setLastUpdate(new Date().toLocaleString());
        
        const quality = checkDataQuality(processedData);
        setDataQuality(quality);
        
        const uniqueGrades = [...new Set(processedData.map(item => 
          showNormalizedGrades ? item.正規グレード : item.元グレード
        ))].filter(Boolean);
        setSelectedGrades(uniqueGrades);
        
        const prices = processedData.map(item => item.price).filter(Boolean);
        if (prices.length > 0) {
          const minPrice = Math.floor(Math.min(...prices) / 100) * 100;
          const maxPrice = Math.ceil(Math.max(...prices) / 100) * 100;
          setPriceRange([minPrice, maxPrice]);
        }
        
        setLoading(false);
      },
      error: (error) => {
        console.error('CSV解析エラー:', error);
        setLoading(false);
        alert('CSVファイルの読み込みに失敗しました。');
      }
    });
  };

  if (loading || carTypeLoading) {
    return (
      <div className="flex items-center justify-center h-screen bg-gray-50">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto"></div>
          <p className="mt-4 text-gray-600">
            {carTypeLoading ? '車種を検索中...' : 'データを読み込み中...'}
          </p>
        </div>
      </div>
    );
  }

  // データが選択されていない場合の表示
  if (!selectedCarType || !selectedFile) {
    return (
      <div className="min-h-screen bg-gray-50">
        <div className="bg-white shadow">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            <div className="flex items-center justify-between py-4">
              <div className="flex items-center space-x-4">
                <Car className="h-8 w-8 text-blue-600" />
                <h1 className="text-2xl font-bold text-gray-900">中古車分析ダッシュボード</h1>
              </div>
            </div>
          </div>
        </div>
        
        <div className="flex items-center justify-center" style={{ height: 'calc(100vh - 120px)' }}>
          <div className="text-center bg-white p-12 rounded-lg shadow-lg">
            <Car className="h-16 w-16 text-gray-400 mx-auto mb-4" />
            <h2 className="text-2xl font-semibold text-gray-900 mb-2">車種とデータを選択</h2>
            <p className="text-gray-600">上部のドロップダウンから分析したい車種を選択してください。</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="bg-white shadow">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between py-4">
            <div className="flex items-center space-x-4">
              <Car className="h-8 w-8 text-blue-600" />
              <h1 className="text-2xl font-bold text-gray-900">中古車分析ダッシュボード</h1>
            </div>
            <div className="flex items-center space-x-4">
              <select
                value={selectedCarType}
                onChange={(e) => setSelectedCarType(e.target.value)}
                className="px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                disabled={carTypeLoading}
              >
                {availableCarTypes.map(carType => (
                  <option key={carType} value={carType}>
                    {carType === 'F' ? 'RC F' : carType}
                  </option>
                ))}
              </select>
              
              <select
                value={selectedFile}
                onChange={(e) => setSelectedFile(e.target.value)}
                className="px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                disabled={loading}
              >
                {availableFiles.map(file => (
                  <option key={file.path} value={file.path}>
                    {file.displayName}
                  </option>
                ))}
              </select>
              
              {dataQuality && (
                <div className="flex items-center space-x-2">
                  {dataQuality.score >= 90 ? (
                    <Check className="h-5 w-5 text-green-500" />
                  ) : (
                    <AlertCircle className="h-5 w-5 text-yellow-500" />
                  )}
                  <span className="text-sm text-gray-600">品質: {dataQuality.score}%</span>
                </div>
              )}
              
              <button
                onClick={handleExportData}
                className="px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 transition-colors flex items-center"
              >
                <Download className="h-4 w-4 mr-2" />
                エクスポート
              </button>
              
              <input
                ref={fileInputRef}
                type="file"
                accept=".csv"
                onChange={handleFileUpload}
                className="hidden"
              />
              
              <button
                onClick={() => fileInputRef.current?.click()}
                className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors flex items-center"
              >
                <Upload className="h-4 w-4 mr-2" />
                CSVアップロード
              </button>
              
              <span className="text-sm text-gray-600">
                表示中: <span className="font-semibold text-blue-600">{filteredData.length}</span>件 / 
                全<span className="font-semibold">{rawData.length}</span>件
              </span>
            </div>
          </div>
        </div>
      </div>
      
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
        <div className="grid grid-cols-4 gap-6">
          {/* サイドバー - フィルター */}
          <div className="col-span-1">
            <div className="bg-white rounded-lg shadow p-6 sticky top-6">
              <div className="flex items-center justify-between mb-4">
                <h2 className="text-lg font-semibold text-gray-900 flex items-center">
                  <Filter className="h-5 w-5 mr-2" />
                  フィルター
                </h2>
                <button
                  onClick={() => {
                    setYearRange({ min: 2014, max: 2025 });
                    setPriceRange([0, 2000]);
                    setMileageMax(200000);
                    setTransmissionFilters({ AT: true, CVT: true, MT: true, other: true });
                    setRepairHistoryFilter('all');
                    setSelectedGrades(availableGrades);
                  }}
                  className="text-sm text-blue-600 hover:text-blue-800"
                >
                  リセット
                </button>
              </div>

              {/* グレード表示切り替え */}
              <div className="mb-4">
                <label className="block text-sm font-medium text-gray-700 mb-2">グレード表示</label>
                <div className="space-y-2">
                  <label className="flex items-center">
                    <input
                      type="radio"
                      checked={showNormalizedGrades}
                      onChange={() => handleGradeTypeChange(true)}
                      className="mr-2"
                    />
                    <span className="text-sm">正規グレード</span>
                  </label>
                  <label className="flex items-center">
                    <input
                      type="radio"
                      checked={!showNormalizedGrades}
                      onChange={() => handleGradeTypeChange(false)}
                      className="mr-2"
                    />
                    <span className="text-sm">元グレード</span>
                  </label>
                </div>
              </div>

              {/* グレード選択 */}
              <div className="mb-4">
                <div className="flex justify-between items-center mb-2">
                  <label className="text-sm font-medium text-gray-700">グレード選択</label>
                  <button
                    onClick={handleSelectAllGrades}
                    className="text-xs text-blue-600 hover:text-blue-800"
                  >
                    {selectedGrades.length === availableGrades.length ? '全解除' : '全選択'}
                  </button>
                </div>
                <div className="space-y-1 max-h-40 overflow-y-auto border border-gray-200 rounded p-2">
                  {availableGrades.map(grade => (
                    <label key={grade} className="flex items-center text-sm">
                      <input
                        type="checkbox"
                        checked={selectedGrades.includes(grade)}
                        onChange={() => handleGradeToggle(grade)}
                        className="mr-2"
                      />
                      <span className="truncate" title={grade}>{grade}</span>
                    </label>
                  ))}
                </div>
              </div>

              {/* 年式範囲 */}
              <div className="mb-4">
                <label className="block text-sm font-medium text-gray-700 mb-2">年式範囲</label>
                <div className="flex items-center space-x-2">
                  <input
                    type="number"
                    value={yearRange.min}
                    onChange={(e) => setYearRange({ ...yearRange, min: parseInt(e.target.value) || 2014 })}
                    className="w-20 px-2 py-1 border border-gray-300 rounded"
                    min="2014"
                    max="2025"
                  />
                  <span className="text-gray-500">〜</span>
                  <input
                    type="number"
                    value={yearRange.max}
                    onChange={(e) => setYearRange({ ...yearRange, max: parseInt(e.target.value) || 2025 })}
                    className="w-20 px-2 py-1 border border-gray-300 rounded"
                    min="2014"
                    max="2025"
                  />
                </div>
              </div>

              {/* 価格範囲 */}
              <div className="mb-4">
                <RangeSlider
                  min={0}
                  max={2000}
                  value={priceRange}
                  onChange={setPriceRange}
                  step={50}
                  label="価格範囲"
                  unit="万円"
                />
              </div>

              {/* 走行距離 */}
              <div className="mb-4">
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  走行距離上限: {(mileageMax / 10000).toFixed(1)}万km
                </label>
                <input
                  type="range"
                  min="0"
                  max="200000"
                  step="10000"
                  value={mileageMax}
                  onChange={(e) => setMileageMax(parseInt(e.target.value))}
                  className="w-full"
                />
              </div>

              {/* ミッション */}
              <div className="mb-4">
                <label className="block text-sm font-medium text-gray-700 mb-2">ミッション</label>
                <div className="space-y-1">
                  {Object.entries({
                    AT: 'AT（オートマ）',
                    CVT: 'CVT',
                    MT: 'MT（マニュアル）',
                    other: 'その他'
                  }).map(([key, label]) => (
                    <label key={key} className="flex items-center text-sm">
                      <input
                        type="checkbox"
                        checked={transmissionFilters[key]}
                        onChange={() => handleTransmissionToggle(key)}
                        className="mr-2"
                      />
                      {label}
                    </label>
                  ))}
                </div>
              </div>

              {/* 修復歴 */}
              <div className="mb-4">
                <label className="block text-sm font-medium text-gray-700 mb-2">修復歴</label>
                <div className="space-y-1">
                  <label className="flex items-center text-sm">
                    <input
                      type="radio"
                      checked={repairHistoryFilter === 'all'}
                      onChange={() => setRepairHistoryFilter('all')}
                      name="repairHistory"
                      className="mr-2"
                    />
                    すべて
                  </label>
                  <label className="flex items-center text-sm">
                    <input
                      type="radio"
                      checked={repairHistoryFilter === 'none'}
                      onChange={() => setRepairHistoryFilter('none')}
                      name="repairHistory"
                      className="mr-2"
                    />
                    修復歴なし
                  </label>
                  <label className="flex items-center text-sm">
                    <input
                      type="radio"
                      checked={repairHistoryFilter === 'exists'}
                      onChange={() => setRepairHistoryFilter('exists')}
                      name="repairHistory"
                      className="mr-2"
                    />
                    修復歴あり
                  </label>
                </div>
              </div>

              <div className="pt-4 border-t text-sm text-gray-600 text-center">
                {filteredData.length}件 / {rawData.length}件
              </div>
            </div>
          </div>

          {/* メインコンテンツ */}
          <div className="col-span-3 space-y-6">
            {/* 表示モード選択 */}
            <div className="bg-white rounded-lg shadow p-4">
              <div className="flex justify-between items-center">
                <div className="flex space-x-2">
                  {[
                    { key: 'overview', label: '概要', icon: Info },
                    { key: 'trend', label: '価格推移', icon: TrendingUp },
                    { key: 'grades', label: 'グレード分析', icon: Package },
                    { key: 'scatter', label: '走行距離vs価格', icon: Activity }
                  ].map(({ key, label, icon: Icon }) => (
                    <button
                      key={key}
                      onClick={() => setViewMode(key)}
                      className={`px-4 py-2 rounded-lg flex items-center transition-colors ${
                        viewMode === key
                          ? 'bg-blue-600 text-white'
                          : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                      }`}
                    >
                      <Icon className="h-4 w-4 mr-2" />
                      {label}
                    </button>
                  ))}
                </div>

                {viewMode === 'overview' && (
                  <div className="flex space-x-2">
                    <button
                      onClick={() => setOverviewMode('all')}
                      className={`px-3 py-1 rounded text-sm ${
                        overviewMode === 'all' ? 'bg-green-600 text-white' : 'bg-gray-200 text-gray-700'
                      }`}
                    >
                      全期間
                    </button>
                    <button
                      onClick={() => setOverviewMode('latest')}
                      className={`px-3 py-1 rounded text-sm ${
                        overviewMode === 'latest' ? 'bg-green-600 text-white' : 'bg-gray-200 text-gray-700'
                      }`}
                    >
                      最新
                    </button>
                  </div>
                )}
              </div>
            </div>

            {/* 概要モード */}
            {viewMode === 'overview' && (
              <>
                {/* 統計カード */}
                <div className="grid grid-cols-5 gap-4">
                  <div className="bg-white rounded-lg shadow p-4">
                    <div className="flex items-center justify-between">
                      <div>
                        <p className="text-sm text-gray-600">平均価格</p>
                        <p className="text-2xl font-bold text-gray-900">
                          {filteredData.length > 0 
                            ? `${Math.round(filteredData.reduce((sum, item) => sum + item.price, 0) / filteredData.length)}万円`
                            : '-'
                          }
                        </p>
                      </div>
                      <DollarSign className="h-8 w-8 text-green-500" />
                    </div>
                  </div>

                  <div className="bg-white rounded-lg shadow p-4">
                    <div className="flex items-center justify-between">
                      <div>
                        <p className="text-sm text-gray-600">価格帯</p>
                        <p className="text-2xl font-bold text-gray-900">
                          {filteredData.length > 0 
                            ? `${Math.min(...filteredData.map(item => item.price))}-${Math.max(...filteredData.map(item => item.price))}万`
                            : '-'
                          }
                        </p>
                      </div>
                      <TrendingUp className="h-8 w-8 text-blue-500" />
                    </div>
                  </div>

                  <div className="bg-white rounded-lg shadow p-4">
                    <div className="flex items-center justify-between">
                      <div>
                        <p className="text-sm text-gray-600">グレード数</p>
                        <p className="text-2xl font-bold text-gray-900">{availableGrades.length}</p>
                      </div>
                      <Package className="h-8 w-8 text-purple-500" />
                    </div>
                  </div>

                  <div className="bg-white rounded-lg shadow p-4">
                    <div className="flex items-center justify-between">
                      <div>
                        <p className="text-sm text-gray-600">平均年式</p>
                        <p className="text-2xl font-bold text-gray-900">
                          {filteredData.length > 0 
                            ? `${Math.round(filteredData.reduce((sum, item) => sum + item.year, 0) / filteredData.length)}年`
                            : '-'
                          }
                        </p>
                      </div>
                      <Calendar className="h-8 w-8 text-orange-500" />
                    </div>
                  </div>

                  <div className="bg-white rounded-lg shadow p-4">
                    <div className="flex items-center justify-between">
                      <div>
                        <p className="text-sm text-gray-600">修復歴なし</p>
                        <p className="text-2xl font-bold text-gray-900">
                          {filteredData.length > 0 
                            ? `${Math.round(filteredData.filter(item => item.修復歴 === 'なし').length / filteredData.length * 100)}%`
                            : '-'
                          }
                        </p>
                      </div>
                      <Check className="h-8 w-8 text-green-500" />
                    </div>
                  </div>
                </div>

                {/* グラフ */}
                <div className="grid grid-cols-2 gap-6">
                  {/* 年式分布 */}
                  <div className="bg-white rounded-lg shadow p-6">
                    <h3 className="text-lg font-semibold text-gray-900 mb-4">年式分布</h3>
                    <ResponsiveContainer width="100%" height={300}>
                      <BarChart data={yearDistributionData}>
                        <CartesianGrid strokeDasharray="3 3" />
                        <XAxis dataKey="year" />
                        <YAxis />
                        <Tooltip />
                        <Bar dataKey="count" fill="#3B82F6" />
                      </BarChart>
                    </ResponsiveContainer>
                  </div>

                  {/* 修復歴統計 */}
                  <div className="bg-white rounded-lg shadow p-6">
                    <h3 className="text-lg font-semibold text-gray-900 mb-4">修復歴統計</h3>
                    <ResponsiveContainer width="100%" height={300}>
                      <PieChart>
                        <Pie
                          data={repairHistoryData}
                          dataKey="value"
                          nameKey="name"
                          cx="50%"
                          cy="50%"
                          outerRadius={80}
                          label
                        >
                          {repairHistoryData.map((entry, index) => (
                            <Cell key={`cell-${index}`} fill={entry.fill} />
                          ))}
                        </Pie>
                        <Tooltip />
                      </PieChart>
                    </ResponsiveContainer>
                  </div>
                </div>
              </>
            )}

            {/* 価格推移モード */}
            {viewMode === 'trend' && (
              <div className="bg-white rounded-lg shadow p-6">
                <div className="flex justify-between items-center mb-4">
                  <h3 className="text-lg font-semibold text-gray-900">価格推移</h3>
                  <div className="flex space-x-4">
                    <div className="flex space-x-2">
                      <button
                        onClick={() => setTrendDateMode('scraping')}
                        className={`px-3 py-1 rounded text-sm ${
                          trendDateMode === 'scraping' ? 'bg-green-600 text-white' : 'bg-gray-200 text-gray-700'
                        }`}
                      >
                        取得日基準
                      </button>
                      <button
                        onClick={() => setTrendDateMode('year')}
                        className={`px-3 py-1 rounded text-sm ${
                          trendDateMode === 'year' ? 'bg-green-600 text-white' : 'bg-gray-200 text-gray-700'
                        }`}
                      >
                        年式基準
                      </button>
                    </div>
                    
                    {trendDateMode === 'scraping' && (
                      <div className="flex space-x-2">
                        <button
                          onClick={() => setTimeScale('daily')}
                          className={`px-3 py-1 rounded text-sm ${
                            timeScale === 'daily' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-700'
                          }`}
                        >
                          日次
                        </button>
                        <button
                          onClick={() => setTimeScale('monthly')}
                          className={`px-3 py-1 rounded text-sm ${
                            timeScale === 'monthly' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-700'
                          }`}
                        >
                          月次
                        </button>
                        <button
                          onClick={() => setTimeScale('yearly')}
                          className={`px-3 py-1 rounded text-sm ${
                            timeScale === 'yearly' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-700'
                          }`}
                        >
                          年次
                        </button>
                      </div>
                    )}
                  </div>
                </div>
                <ResponsiveContainer width="100%" height={400}>
                  <LineChart data={trendData}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="date" />
                    <YAxis />
                    <Tooltip />
                    <Legend />
                    <Line type="monotone" dataKey="avgPrice" stroke="#3B82F6" name="平均価格" strokeWidth={2} />
                    <Line type="monotone" dataKey="median" stroke="#10B981" name="中央値" strokeWidth={2} />
                    <Line type="monotone" dataKey="maxPrice" stroke="#EF4444" name="最高価格" strokeDasharray="5 5" />
                    <Line type="monotone" dataKey="minPrice" stroke="#F59E0B" name="最低価格" strokeDasharray="5 5" />
                  </LineChart>
                </ResponsiveContainer>
              </div>
            )}

            {/* グレード分析モード */}
            {viewMode === 'grades' && (
              <div className="space-y-6">
                <div className="bg-white rounded-lg shadow p-6">
                  <h3 className="text-lg font-semibold text-gray-900 mb-4">グレード別価格比較</h3>
                  <ResponsiveContainer width="100%" height={400}>
                    <BarChart data={gradeAnalysisData} margin={{ bottom: 100 }}>
                      <CartesianGrid strokeDasharray="3 3" />
                      <XAxis 
                        dataKey="grade" 
                        angle={-45} 
                        textAnchor="end"
                        interval={0}
                      />
                      <YAxis />
                      <Tooltip />
                      <Legend />
                      <Bar dataKey="avgPrice" fill="#3B82F6" name="平均価格" />
                      <Bar dataKey="median" fill="#10B981" name="中央値" />
                    </BarChart>
                  </ResponsiveContainer>
                </div>

                <div className="bg-white rounded-lg shadow p-6">
                  <h3 className="text-lg font-semibold text-gray-900 mb-4">グレード詳細統計</h3>
                  <div className="overflow-x-auto">
                    <table className="w-full text-sm">
                      <thead>
                        <tr className="border-b">
                          <th className="text-left py-2">グレード</th>
                          <th className="text-right py-2">台数</th>
                          <th className="text-right py-2">平均価格</th>
                          <th className="text-right py-2">中央値</th>
                          <th className="text-right py-2">最低価格</th>
                          <th className="text-right py-2">最高価格</th>
                        </tr>
                      </thead>
                      <tbody>
                        {gradeAnalysisData.map(grade => (
                          <tr key={grade.grade} className="border-b hover:bg-gray-50">
                            <td className="py-2">{grade.grade}</td>
                            <td className="text-right py-2">{grade.count}</td>
                            <td className="text-right py-2">{grade.avgPrice}万円</td>
                            <td className="text-right py-2">{grade.median}万円</td>
                            <td className="text-right py-2">{grade.minPrice}万円</td>
                            <td className="text-right py-2">{grade.maxPrice}万円</td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                </div>
              </div>
            )}

            {/* 走行距離vs価格モード */}
            {viewMode === 'scatter' && (
              <div className="bg-white rounded-lg shadow p-6">
                <h3 className="text-lg font-semibold text-gray-900 mb-4">走行距離 vs 価格</h3>
                <ResponsiveContainer width="100%" height={500}>
                  <ScatterChart>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis 
                      dataKey="mileage" 
                      name="走行距離" 
                      unit="km"
                      tickFormatter={(value) => `${(value / 10000).toFixed(1)}万`}
                    />
                    <YAxis dataKey="price" name="価格" unit="万円" />
                    <Tooltip 
                      cursor={{ strokeDasharray: '3 3' }}
                      content={({ active, payload }) => {
                        if (active && payload && payload.length) {
                          const data = payload[0].payload;
                          return (
                            <div className="bg-white p-3 border border-gray-200 rounded shadow-lg">
                              <p className="font-semibold text-sm mb-2">車両詳細</p>
                              <p className="text-sm">価格: {data.price}万円</p>
                              <p className="text-sm">走行距離: {(data.mileage / 10000).toFixed(1)}万km</p>
                              <p className="text-sm">年式: {data.year}年</p>
                              <p className="text-sm">グレード: {data.グレード}</p>
                              <p className="text-sm">修復歴: {data.修復歴}</p>
                              <button
                                onClick={() => handleCarClick(data)}
                                className="mt-2 text-blue-600 hover:text-blue-800 text-sm underline"
                              >
                                詳細を見る
                              </button>
                            </div>
                          );
                        }
                        return null;
                      }}
                    />
                    <Scatter 
                      name="車両" 
                      data={filteredData} 
                      fill="#3B82F6"
                      onClick={(data) => handleCarClick(data)}
                    />
                  </ScatterChart>
                </ResponsiveContainer>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* 車両詳細モーダル */}
      {showCarDetailModal && selectedCarDetail && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-lg max-w-2xl w-full max-h-[90vh] overflow-y-auto">
            <div className="p-6">
              <div className="flex items-center justify-between mb-4">
                <h3 className="text-xl font-semibold">車両詳細情報</h3>
                <button
                  onClick={() => setShowCarDetailModal(false)}
                  className="text-gray-400 hover:text-gray-600"
                >
                  <X className="h-6 w-6" />
                </button>
              </div>
              
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <p className="text-sm text-gray-600">価格</p>
                  <p className="text-lg font-semibold">{selectedCarDetail.price}万円</p>
                </div>
                <div>
                  <p className="text-sm text-gray-600">年式</p>
                  <p className="text-lg font-semibold">{selectedCarDetail.year}年</p>
                </div>
                <div>
                  <p className="text-sm text-gray-600">走行距離</p>
                  <p className="text-lg font-semibold">
                    {selectedCarDetail.mileage ? `${(selectedCarDetail.mileage / 10000).toFixed(1)}万km` : '不明'}
                  </p>
                </div>
                <div>
                  <p className="text-sm text-gray-600">グレード</p>
                  <p className="text-lg font-semibold">{selectedCarDetail.グレード}</p>
                </div>
                <div>
                  <p className="text-sm text-gray-600">修復歴</p>
                  <p className="text-lg font-semibold">{selectedCarDetail.修復歴}</p>
                </div>
                <div>
                  <p className="text-sm text-gray-600">ミッション</p>
                  <p className="text-lg font-semibold">{selectedCarDetail.ミッション}</p>
                </div>
              </div>
              
              {selectedCarDetail.url && selectedCarDetail.url !== '#' && (
                <div className="mt-6">
                  <a
                    href={selectedCarDetail.url}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="inline-flex items-center px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
                  >
                    <ExternalLink className="h-4 w-4 mr-2" />
                    カーセンサーで詳細を見る
                  </a>
                </div>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default CarAnalysisDashboard;
