import React, { useState, useEffect, useMemo } from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ScatterChart, Scatter, ResponsiveContainer, BarChart, Bar, PieChart, Pie, Cell } from 'recharts';
import { Car, TrendingUp, BarChart3, MapPin, Filter, Upload, Download, RefreshCw, AlertCircle, CheckCircle } from 'lucide-react';
import Papa from 'papaparse';
import { 
  processCarData, 
  calculatePriceStats, 
  exportToCSV, 
  checkDataQuality,
  convertToLegacyFormat 
} from '../utils/dataProcessor';

export default function CarAnalysisDashboard() {
  const [rawData, setRawData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [viewMode, setViewMode] = useState('overview');
  const [timeScale, setTimeScale] = useState('monthly');
  const [selectedGrades, setSelectedGrades] = useState([]);
  const [yearRange, setYearRange] = useState({ min: 2014, max: 2025 });
  const [mileageMax, setMileageMax] = useState(200000);
  const [showNormalizedGrades, setShowNormalizedGrades] = useState(true);
  const [fileUploaded, setFileUploaded] = useState(false);
  const [dataQuality, setDataQuality] = useState(null);
  const [lastUpdate, setLastUpdate] = useState(null);

  // 既存データの自動読み込み
  const loadExistingData = async () => {
    setLoading(true);
    try {
      // 既存のRC Fデータを読み込み
      const response = await fetch('/data/rc_f_data.json');
      if (response.ok) {
        const jsonData = await response.json();
        const processedData = processCarData(jsonData);
        setRawData(processedData);
        setFileUploaded(true);
        setLastUpdate(new Date().toLocaleString());
        
        // データ品質チェック
        const quality = checkDataQuality(processedData);
        setDataQuality(quality);
        
        // 初期フィルター設定
        const uniqueGrades = [...new Set(processedData.map(item => 
          showNormalizedGrades ? item.正規グレード : item.元グレード
        ))].filter(Boolean);
        setSelectedGrades(uniqueGrades);
        
        console.log('既存データを読み込みました:', processedData.length, '件');
      } else {
        // フォールバック: サンプルデータを生成
        console.log('既存データが見つかりません。サンプルデータを生成します。');
        generateSampleData();
      }
    } catch (error) {
      console.error('データ読み込みエラー:', error);
      generateSampleData();
    } finally {
      setLoading(false);
    }
  };

  // サンプルデータ生成（既存CSVデータの構造に合わせる）
  const generateSampleData = () => {
    const grades = [
      'RC カーボンエクステリアパッケージ',
      'RC 5.0',
      'RC パフォーマンスパッケージ',
      'RC F 10th アニバーサリー',
      'RC ファイナル エディション',
      'RC エモーショナル ツーリング'
    ];
    
    const normalizedGrades = [
      'カーボンエクステリア',
      '5.0',
      'パフォーマンス',
      '10th アニバーサリー',
      'ファイナル エディション',
      'エモーショナル ツーリング'
    ];
    
    const years = [2014, 2015, 2016, 2018, 2019, 2020, 2021, 2022, 2023, 2024, 2025];
    const transmissions = ['フロアMTモード付8AT', 'フロア8AT'];
    
    const sampleData = [];
    for (let i = 0; i < 80; i++) {
      const gradeIndex = Math.floor(Math.random() * grades.length);
      const year = years[Math.floor(Math.random() * years.length)];
      const mileage = Math.random() * 150000;
      
      // より現実的な価格設定
      let basePrice = 300;
      if (normalizedGrades[gradeIndex].includes('エモーショナル') || normalizedGrades[gradeIndex].includes('ファイナル')) {
        basePrice = 1200;
      } else if (normalizedGrades[gradeIndex].includes('10th')) {
        basePrice = 900;
      } else if (normalizedGrades[gradeIndex].includes('パフォーマンス')) {
        basePrice = 800;
      } else if (normalizedGrades[gradeIndex].includes('カーボン')) {
        basePrice = 600;
      }
      
      const yearFactor = Math.max(0.5, 1 - ((2025 - year) * 0.05));
      const mileageFactor = Math.max(0.6, 1 - (mileage / 100000) * 0.2);
      const price = Math.round(basePrice * yearFactor * mileageFactor * (0.8 + Math.random() * 0.4) * 10) / 10;
      
      const month = Math.floor(Math.random() * 12) + 1;
      const day = Math.floor(Math.random() * 28) + 1;
      
      sampleData.push({
        車種名: 'F',
        モデル: '情報なし',
        グレード: grades[gradeIndex],
        正規グレード: normalizedGrades[gradeIndex],
        支払総額: `${price}万円`,
        年式: `${year}(${year >= 2019 ? 'R' + String(year - 2018).padStart(2, '0') : 'H' + (year - 1988)})`,
        走行距離: `${Math.round(mileage / 10000 * 10) / 10}万km`,
        修復歴: Math.random() > 0.85 ? 'あり' : 'なし',
        ミッション: transmissions[Math.floor(Math.random() * transmissions.length)],
        排気量: '5000CC',
        マッチング精度: 0.7 + Math.random() * 0.3,
        取得日時: `2024-${String(month).padStart(2, '0')}-${String(day).padStart(2, '0')}T12:45:11.583278`,
        ソースURL: 'https://www.carsensor.net/usedcar/bLE/s016/index.html'
      });
    }
    
    const processedData = processCarData(sampleData);
    setRawData(processedData);
    setFileUploaded(false);
    setLastUpdate(new Date().toLocaleString());
    
    // データ品質チェック
    const quality = checkDataQuality(processedData);
    setDataQuality(quality);
    
    // 初期フィルター設定
    const uniqueGrades = [...new Set(processedData.map(item => 
      showNormalizedGrades ? item.正規グレード : item.元グレード
    ))].filter(Boolean);
    setSelectedGrades(uniqueGrades);
  };

  // CSVファイルアップロード処理
  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (!file) return;

    setLoading(true);
    Papa.parse(file, {
      header: true,
      encoding: 'UTF-8',
      complete: (results) => {
        console.log('アップロードされたCSV:', results.data);
        const processedData = processCarData(results.data);
        setRawData(processedData);
        setFileUploaded(true);
        setLastUpdate(new Date().toLocaleString());
        
        // データ品質チェック
        const quality = checkDataQuality(processedData);
        setDataQuality(quality);
        
        // 初期フィルター設定
        const uniqueGrades = [...new Set(processedData.map(item => 
          showNormalizedGrades ? item.正規グレード : item.元グレード
        ))].filter(Boolean);
        setSelectedGrades(uniqueGrades);
        setLoading(false);
      },
      error: (error) => {
        console.error('CSV解析エラー:', error);
        setLoading(false);
        alert('CSVファイルの読み込みに失敗しました。');
      }
    });
  };

  // 初期データ読み込み
  useEffect(() => {
    loadExistingData();
  }, []);

  // フィルター適用後のデータ
  const filteredData = useMemo(() => {
    return rawData.filter(item => {
      const targetGrade = showNormalizedGrades ? item.正規グレード : item.元グレード;
      
      if (selectedGrades.length > 0 && !selectedGrades.includes(targetGrade)) return false;
      if (item.year < yearRange.min || item.year > yearRange.max) return false;
      if (item.mileage > mileageMax) return false;
      return true;
    });
  }, [rawData, selectedGrades, yearRange, mileageMax, showNormalizedGrades]);

  // 価格推移データ
  const trendData = useMemo(() => {
    if (filteredData.length === 0) return [];
    
    const grouped = {};
    filteredData.forEach(item => {
      let key;
      if (timeScale === 'monthly') {
        key = item.date.substring(0, 7);
      } else {
        key = item.year.toString();
      }
      
      if (!grouped[key]) {
        grouped[key] = [];
      }
      grouped[key].push(item);
    });

    return Object.entries(grouped)
      .map(([key, items]) => {
        const stats = calculatePriceStats(items.map(item => item.price));
        return {
          date: key,
          avgPrice: stats.mean,
          count: items.length,
          maxPrice: stats.max,
          minPrice: stats.min,
          median: stats.median
        };
      })
      .sort((a, b) => a.date.localeCompare(b.date));
  }, [filteredData, timeScale]);

  // グレード別分析データ
  const gradeAnalysisData = useMemo(() => {
    if (filteredData.length === 0) return [];
    
    const gradeStats = {};
    filteredData.forEach(item => {
      const grade = showNormalizedGrades ? item.正規グレード : item.元グレード;
      if (!gradeStats[grade]) {
        gradeStats[grade] = {
          prices: [],
          years: [],
          mileages: [],
          matchingScores: []
        };
      }
      gradeStats[grade].prices.push(item.price);
      gradeStats[grade].years.push(item.year);
      gradeStats[grade].mileages.push(item.mileage);
      gradeStats[grade].matchingScores.push(item.マッチング精度);
    });

    return Object.entries(gradeStats)
      .map(([grade, stats]) => {
        const priceStats = calculatePriceStats(stats.prices);
        return {
          grade: grade.length > 15 ? grade.substring(0, 15) + '...' : grade,
          fullGrade: grade,
          avgPrice: priceStats.mean,
          minPrice: priceStats.min,
          maxPrice: priceStats.max,
          median: priceStats.median,
          count: priceStats.count,
          avgYear: Math.round(stats.years.reduce((sum, year) => sum + year, 0) / stats.years.length),
          avgMileage: Math.round(stats.mileages.reduce((sum, mileage) => sum + mileage, 0) / stats.mileages.length),
          avgMatchingScore: Math.round(stats.matchingScores.reduce((sum, score) => sum + score, 0) / stats.matchingScores.length * 100) / 100
        };
      })
      .sort((a, b) => b.avgPrice - a.avgPrice);
  }, [filteredData, showNormalizedGrades]);

  // 走行距離 vs 価格データ
  const mileagePriceData = useMemo(() => {
    return filteredData.map(item => ({
      ...item,
      mileageKm: Math.round(item.mileage / 1000),
      yearColor: item.year >= 2020 ? '#3b82f6' : item.year >= 2017 ? '#10b981' : '#ef4444',
      costPerformance: item.pricePerCC ? item.pricePerCC : 0
    }));
  }, [filteredData]);

  // 年式分布データ
  const yearDistributionData = useMemo(() => {
    const yearCounts = {};
    filteredData.forEach(item => {
      yearCounts[item.year] = (yearCounts[item.year] || 0) + 1;
    });
    
    return Object.entries(yearCounts)
      .map(([year, count]) => ({ year: parseInt(year), count }))
      .sort((a, b) => a.year - b.year);
  }, [filteredData]);

  // 修復歴統計
  const repairHistoryData = useMemo(() => {
    const stats = { あり: 0, なし: 0 };
    filteredData.forEach(item => {
      stats[item.修復歴] = (stats[item.修復歴] || 0) + 1;
    });
    
    return [
      { name: '修復歴なし', value: stats.なし, fill: '#10b981' },
      { name: '修復歴あり', value: stats.あり, fill: '#ef4444' }
    ];
  }, [filteredData]);

  // 利用可能なグレード一覧
  const availableGrades = useMemo(() => {
    return [...new Set(rawData.map(item => 
      showNormalizedGrades ? item.正規グレード : item.元グレード
    ))].filter(Boolean).sort();
  }, [rawData, showNormalizedGrades]);

  // グレード選択切り替え
  const handleGradeToggle = (grade) => {
    setSelectedGrades(prev => 
      prev.includes(grade) 
        ? prev.filter(g => g !== grade)
        : [...prev, grade]
    );
  };

  // 全選択/全解除
  const handleSelectAllGrades = () => {
    setSelectedGrades(selectedGrades.length === availableGrades.length ? [] : availableGrades);
  };

  // グレード表示切り替え
  const handleGradeTypeChange = (useNormalized) => {
    setShowNormalizedGrades(useNormalized);
    const uniqueGrades = [...new Set(rawData.map(item => 
      useNormalized ? item.正規グレード : item.元グレード
    ))].filter(Boolean);
    setSelectedGrades(uniqueGrades);
  };

  // データエクスポート
  const handleExportData = () => {
    const exportData = convertToLegacyFormat(filteredData);
    exportToCSV(exportData, `rc_f_analysis_${new Date().toISOString().split('T')[0]}.csv`);
  };

  // データ更新
  const handleRefreshData = () => {
    loadExistingData();
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen bg-gray-50">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500 mx-auto mb-4"></div>
          <p className="text-gray-600">データを読み込み中...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* ヘッダー */}
      <div className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-4">
            <div className="flex items-center space-x-3">
              <Car className="h-8 w-8 text-blue-600" />
              <div>
                <h1 className="text-2xl font-bold text-gray-900">RC F 中古車分析ダッシュボード</h1>
                {lastUpdate && (
                  <p className="text-sm text-gray-500">最終更新: {lastUpdate}</p>
                )}
              </div>
            </div>
            <div className="flex items-center space-x-4">
              {/* データ品質インジケーター */}
              {dataQuality && (
                <div className="flex items-center space-x-2">
                  {dataQuality.qualityScore >= 90 ? (
                    <CheckCircle className="h-5 w-5 text-green-500" />
                  ) : (
                    <AlertCircle className="h-5 w-5 text-yellow-500" />
                  )}
                  <span className="text-sm text-gray-600">
                    品質: {dataQuality.qualityScore}%
                  </span>
                </div>
              )}
              
              {/* 操作ボタン */}
              <button
                onClick={handleRefreshData}
                className="flex items-center space-x-2 px-3 py-2 bg-gray-100 text-gray-700 rounded-md hover:bg-gray-200 transition-colors"
              >
                <RefreshCw className="h-4 w-4" />
                <span>更新</span>
              </button>
              
              <button
                onClick={handleExportData}
                className="flex items-center space-x-2 px-3 py-2 bg-green-500 text-white rounded-md hover:bg-green-600 transition-colors"
              >
                <Download className="h-4 w-4" />
                <span>エクスポート</span>
              </button>
              
              {/* ファイルアップロード */}
              <div className="relative">
                <input
                  type="file"
                  accept=".csv"
                  onChange={handleFileUpload}
                  className="absolute inset-0 w-full h-full opacity-0 cursor-pointer"
                />
                <button className="flex items-center space-x-2 px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600 transition-colors">
                  <Upload className="h-4 w-4" />
                  <span>CSVアップロード</span>
                </button>
              </div>
              
              <span className="text-sm text-gray-500">
                表示中: <span className="font-semibold text-blue-600">{filteredData.length}</span>件 / 
                全<span className="font-semibold">{rawData.length}</span>件
              </span>
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
        <div className="grid grid-cols-1 lg:grid-cols-4 gap-6">
          {/* サイドバー - フィルター */}
          <div className="lg:col-span-1">
            <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6 sticky top-6">
              <div className="flex items-center space-x-2 mb-4">
                <Filter className="h-5 w-5 text-gray-600" />
                <h2 className="text-lg font-semibold text-gray-900">フィルター</h2>
              </div>

              {/* データ品質情報 */}
              {dataQuality && (
                <div className="mb-6 p-3 bg-gray-50 rounded-md">
                  <h3 className="text-sm font-medium text-gray-700 mb-2">データ品質</h3>
                  <div className="text-xs text-gray-600 space-y-1">
                    <div>有効データ: {dataQuality.validCount}件</div>
                    <div>品質スコア: {dataQuality.qualityScore}%</div>
                  </div>
                </div>
              )}

              {/* グレード表示切り替え */}
              <div className="mb-6">
                <label className="block text-sm font-medium text-gray-700 mb-2">グレード表示</label>
                <div className="space-y-2">
                  <label className="flex items-center">
                    <input
                      type="radio"
                      checked={showNormalizedGrades}
                      onChange={() => handleGradeTypeChange(true)}
                      className="mr-2 text-blue-600"
                    />
                    <span className="text-sm">正規グレード</span>
                  </label>
                  <label className="flex items-center">
                    <input
                      type="radio"
                      checked={!showNormalizedGrades}
                      onChange={() => handleGradeTypeChange(false)}
                      className="mr-2 text-blue-600"
                    />
                    <span className="text-sm">元グレード</span>
                  </label>
                </div>
              </div>

              {/* グレード選択 */}
              <div className="mb-6">
                <div className="flex justify-between items-center mb-2">
                  <label className="block text-sm font-medium text-gray-700">グレード選択</label>
                  <button
                    onClick={handleSelectAllGrades}
                    className="text-xs text-blue-600 hover:text-blue-800 transition-colors"
                  >
                    {selectedGrades.length === availableGrades.length ? '全解除' : '全選択'}
                  </button>
                </div>
                <div className="max-h-48 overflow-y-auto border border-gray-300 rounded-md p-2 bg-gray-50">
                  {availableGrades.map(grade => (
                    <label key={grade} className="flex items-center mb-1 hover:bg-gray-100 p-1 rounded">
                      <input
                        type="checkbox"
                        checked={selectedGrades.includes(grade)}
                        onChange={() => handleGradeToggle(grade)}
                        className="mr-2 text-blue-600"
                      />
                      <span className="text-sm text-gray-700 truncate" title={grade}>{grade}</span>
                    </label>
                  ))}
                </div>
              </div>

              {/* 年式範囲 */}
              <div className="mb-6">
                <label className="block text-sm font-medium text-gray-700 mb-2">年式範囲</label>
                <div className="flex space-x-2">
                  <input
                    type="number"
                    value={yearRange.min}
                    onChange={(e) => setYearRange(prev => ({ ...prev, min: parseInt(e.target.value) }))}
                    min="2014"
                    max="2025"
                    className="w-20 border border-gray-300 rounded-md px-2 py-1 text-sm focus:outline-none focus:ring-2 focus:ring-blue-500"
                  />
                  <span className="self-center text-gray-500">〜</span>
                  <input
                    type="number"
                    value={yearRange.max}
                    onChange={(e) => setYearRange(prev => ({ ...prev, max: parseInt(e.target.value) }))}
                    min="2014"
                    max="2025"
                    className="w-20 border border-gray-300 rounded-md px-2 py-1 text-sm focus:outline-none focus:ring-2 focus:ring-blue-500"
                  />
                </div>
              </div>

              {/* 走行距離 */}
              <div className="mb-6">
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  走行距離上限: {Math.round(mileageMax / 10000 * 10) / 10}万km
                </label>
                <input
                  type="range"
                  min="0"
                  max="200000"
                  step="10000"
                  value={mileageMax}
                  onChange={(e) => setMileageMax(parseInt(e.target.value))}
                  className="w-full"
                />
              </div>
            </div>
          </div>

          {/* メインコンテンツ */}
          <div className="lg:col-span-3 space-y-6">
            {/* 表示モード選択 */}
            <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-4">
              <div className="flex flex-wrap gap-2">
                {[
                  { key: 'overview', label: '概要', icon: BarChart3 },
                  { key: 'trend', label: '価格推移', icon: TrendingUp },
                  { key: 'grades', label: 'グレード分析', icon: BarChart3 },
                  { key: 'scatter', label: '走行距離vs価格', icon: MapPin }
                ].map(({ key, label, icon: Icon }) => (
                  <button
                    key={key}
                    onClick={() => setViewMode(key)}
                    className={`flex items-center space-x-2 px-4 py-2 rounded-md transition-colors ${
                      viewMode === key 
                        ? 'bg-blue-500 text-white shadow-md' 
                        : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                    }`}
                  >
                    <Icon className="h-4 w-4" />
                    <span>{label}</span>
                  </button>
                ))}
              </div>
            </div>

            {/* 統計カード */}
            <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
              <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
                <div className="text-sm text-gray-500 mb-1">平均価格</div>
                <div className="text-2xl font-bold text-blue-600">
                  {filteredData.length > 0 
                    ? `${Math.round(filteredData.reduce((sum, item) => sum + item.price, 0) / filteredData.length * 10) / 10}万円`
                    : '-'
                  }
                </div>
              </div>
              <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
                <div className="text-sm text-gray-500 mb-1">グレード数</div>
                <div className="text-2xl font-bold text-green-600">
                  {availableGrades.length}種類
                </div>
              </div>
              <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
                <div className="text-sm text-gray-500 mb-1">平均年式</div>
                <div className="text-2xl font-bold text-purple-600">
                  {filteredData.length > 0 
                    ? `${Math.round(filteredData.reduce((sum, item) => sum + item.year, 0) / filteredData.length)}年`
                    : '-'
                  }
                </div>
              </div>
              <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
                <div className="text-sm text-gray-500 mb-1">平均走行距離</div>
                <div className="text-2xl font-bold text-orange-600">
                  {filteredData.length > 0 
                    ? `${Math.round(filteredData.reduce((sum, item) => sum + item.mileage, 0) / filteredData.length / 10000 * 10) / 10}万km`
                    : '-'
                  }
                </div>
              </div>
            </div>

            {/* グラフエリア */}
            {viewMode === 'overview' && (
              <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                {/* 年式分布 */}
                <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
                  <h3 className="text-lg font-semibold text-gray-900 mb-4">年式分布</h3>
                  <ResponsiveContainer width="100%" height={300}>
                    <BarChart data={yearDistributionData}>
                      <CartesianGrid strokeDasharray="3 3" stroke="#f0f0f0" />
                      <XAxis dataKey="year" stroke="#666" />
                      <YAxis stroke="#666" />
                      <Tooltip 
                        formatter={(value) => [`${value}台`, '台数']}
                        labelFormatter={(label) => `${label}年`}
                      />
                      <Bar dataKey="count" fill="#3b82f6" radius={[4, 4, 0, 0]} />
                    </BarChart>
                  </ResponsiveContainer>
                </div>

                {/* 修復歴統計 */}
                <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
                  <h3 className="text-lg font-semibold text-gray-900 mb-4">修復歴統計</h3>
                  <ResponsiveContainer width="100%" height={300}>
                    <PieChart>
                      <Pie
                        data={repairHistoryData}
                        cx="50%"
                        cy="50%"
                        outerRadius={100}
                        dataKey="value"
                        label={({ name, value, percent }) => 
                          `${name}: ${value}台 (${(percent * 100).toFixed(1)}%)`
                        }
                      >
                        {repairHistoryData.map((entry, index) => (
                          <Cell key={`cell-${index}`} fill={entry.fill} />
                        ))}
                      </Pie>
                      <Tooltip />
                    </PieChart>
                  </ResponsiveContainer>
                </div>
              </div>
            )}

            {viewMode === 'trend' && (
              <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
                <div className="flex justify-between items-center mb-4">
                  <h3 className="text-lg font-semibold text-gray-900">価格推移</h3>
                  <div className="flex space-x-2">
                    {['monthly', 'yearly'].map(scale => (
                      <button
                        key={scale}
                        onClick={() => setTimeScale(scale)}
                        className={`px-3 py-1 rounded-md text-sm transition-colors ${
                          timeScale === scale
                            ? 'bg-blue-500 text-white'
                            : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                        }`}
                      >
                        {scale === 'monthly' ? '月次' : '年次'}
                      </button>
                    ))}
                  </div>
                </div>
                <ResponsiveContainer width="100%" height={400}>
                  <LineChart data={trendData}>
                    <CartesianGrid strokeDasharray="3 3" stroke="#f0f0f0" />
                    <XAxis dataKey="date" stroke="#666" />
                    <YAxis label={{ value: '価格 (万円)', angle: -90, position: 'insideLeft' }} stroke="#666" />
                    <Tooltip 
                      formatter={(value, name) => [`${value}万円`, name]}
                      labelFormatter={(label) => `期間: ${label}`}
                    />
                    <Legend />
                    <Line 
                      type="monotone" 
                      dataKey="avgPrice" 
                      stroke="#3b82f6" 
                      strokeWidth={3}
                      name="平均価格"
                      dot={{ fill: '#3b82f6', strokeWidth: 2, r: 4 }}
                    />
                    <Line 
                      type="monotone" 
                      dataKey="median" 
                      stroke="#10b981" 
                      strokeWidth={2}
                      name="中央値"
                      strokeDasharray="5 5"
                      dot={false}
                    />
                    <Line 
                      type="monotone" 
                      dataKey="maxPrice" 
                      stroke="#ef4444" 
                      strokeWidth={1}
                      name="最高価格"
                      strokeDasharray="2 2"
                      dot={false}
                    />
                    <Line 
                      type="monotone" 
                      dataKey="minPrice" 
                      stroke="#f59e0b" 
                      strokeWidth={1}
                      name="最低価格"
                      strokeDasharray="2 2"
                      dot={false}
                    />
                  </LineChart>
                </ResponsiveContainer>
              </div>
            )}

            {viewMode === 'grades' && (
              <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
                <h3 className="text-lg font-semibold text-gray-900 mb-4">グレード別価格分析</h3>
                <ResponsiveContainer width="100%" height={400}>
                  <BarChart data={gradeAnalysisData} margin={{ bottom: 100 }}>
                    <CartesianGrid strokeDasharray="3 3" stroke="#f0f0f0" />
                    <XAxis 
                      dataKey="grade" 
                      angle={-45} 
                      textAnchor="end" 
                      height={100} 
                      stroke="#666"
                      interval={0}
                    />
                    <YAxis label={{ value: '価格 (万円)', angle: -90, position: 'insideLeft' }} stroke="#666" />
                    <Tooltip 
                      formatter={(value, name) => [`${value}万円`, name]}
                      labelFormatter={(label) => {
                        const item = gradeAnalysisData.find(d => d.grade === label);
                        return item ? item.fullGrade : label;
                      }}
                    />
                    <Legend />
                    <Bar dataKey="avgPrice" fill="#3b82f6" name="平均価格" radius={[4, 4, 0, 0]} />
                    <Bar dataKey="median" fill="#10b981" name="中央値" radius={[4, 4, 0, 0]} />
                  </BarChart>
                </ResponsiveContainer>
              </div>
            )}

            {viewMode === 'scatter' && (
              <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
                <h3 className="text-lg font-semibold text-gray-900 mb-4">走行距離 vs 価格</h3>
                <ResponsiveContainer width="100%" height={400}>
                  <ScatterChart data={mileagePriceData}>
                    <CartesianGrid strokeDasharray="3 3" stroke="#f0f0f0" />
                    <XAxis 
                      type="number" 
                      dataKey="mileageKm" 
                      name="走行距離"
                      label={{ value: '走行距離 (千km)', position: 'insideBottom', offset: -5 }}
                      stroke="#666"
                    />
                    <YAxis 
                      type="number" 
                      dataKey="price" 
                      name="価格"
                      label={{ value: '価格 (万円)', angle: -90, position: 'insideLeft' }}
                      stroke="#666"
                    />
                    <Tooltip 
                      cursor={{ strokeDasharray: '3 3' }}
                      content={({ active, payload }) => {
                        if (active && payload && payload.length > 0) {
                          const data = payload[0].payload;
                          return (
                            <div className="bg-white border border-gray-300 rounded-lg p-3 shadow-lg">
                              <p className="text-sm"><strong>価格:</strong> {data.price}万円</p>
                              <p className="text-sm"><strong>走行距離:</strong> {Math.round(data.mileage / 10000 * 10) / 10}万km</p>
                              <p className="text-sm"><strong>年式:</strong> {data.year}年</p>
                              <p className="text-sm"><strong>グレード:</strong> {data.正規グレード}</p>
                              <p className="text-sm"><strong>修復歴:</strong> {data.修復歴}</p>
                            </div>
                          );
                        }
                        return null;
                      }}
                    />
                    <Scatter dataKey="price" fill="#3b82f6" />
                  </ScatterChart>
                </ResponsiveContainer>
                <div className="mt-4 text-sm text-gray-600">
                  <p>💡 <strong>ヒント:</strong> 各点は1台の車両を表しています。左下ほど高コスパ、右上ほど価格が高い傾向があります。</p>
                </div>
              </div>
            )}

            {/* グレード別詳細テーブル */}
            {viewMode === 'grades' && (
              <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
                <h3 className="text-lg font-semibold text-gray-900 mb-4">グレード別詳細データ</h3>
                <div className="overflow-x-auto">
                  <table className="w-full border-collapse border border-gray-300">
                    <thead>
                      <tr className="bg-gray-50">
                        <th className="border border-gray-300 px-4 py-2 text-left">グレード</th>
                        <th className="border border-gray-300 px-4 py-2 text-left">台数</th>
                        <th className="border border-gray-300 px-4 py-2 text-left">平均価格</th>
                        <th className="border border-gray-300 px-4 py-2 text-left">中央値</th>
                        <th className="border border-gray-300 px-4 py-2 text-left">価格範囲</th>
                        <th className="border border-gray-300 px-4 py-2 text-left">平均年式</th>
                        <th className="border border-gray-300 px-4 py-2 text-left">平均走行距離</th>
                        <th className="border border-gray-300 px-4 py-2 text-left">マッチング精度</th>
                      </tr>
                    </thead>
                    <tbody>
                      {gradeAnalysisData.map((item, index) => (
                        <tr key={index} className="hover:bg-gray-50">
                          <td className="border border-gray-300 px-4 py-2" title={item.fullGrade}>
                            {item.fullGrade}
                          </td>
                          <td className="border border-gray-300 px-4 py-2">{item.count}台</td>
                          <td className="border border-gray-300 px-4 py-2 font-semibold text-blue-600">
                            {item.avgPrice}万円
                          </td>
                          <td className="border border-gray-300 px-4 py-2 text-green-600">
                            {item.median}万円
                          </td>
                          <td className="border border-gray-300 px-4 py-2">
                            {item.minPrice}〜{item.maxPrice}万円
                          </td>
                          <td className="border border-gray-300 px-4 py-2">{item.avgYear}年</td>
                          <td className="border border-gray-300 px-4 py-2">
                            {Math.round(item.avgMileage / 10000 * 10) / 10}万km
                          </td>
                          <td className="border border-gray-300 px-4 py-2">
                            <span className={`px-2 py-1 rounded-full text-xs ${
                              item.avgMatchingScore >= 0.9 
                                ? 'bg-green-100 text-green-800' 
                                : item.avgMatchingScore >= 0.8 
                                ? 'bg-yellow-100 text-yellow-800' 
                                : 'bg-red-100 text-red-800'
                            }`}>
                              {Math.round(item.avgMatchingScore * 100)}%
                            </span>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
