import React, { useState, useEffect, useMemo } from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ScatterChart, Scatter, ResponsiveContainer, BarChart, Bar, PieChart, Pie, Cell } from 'recharts';
import { Car, TrendingUp, BarChart3, MapPin, Filter, Upload, Download, RefreshCw, AlertCircle, CheckCircle } from 'lucide-react';
import Papa from 'papaparse';
import { 
  processCarData, 
  calculatePriceStats, 
  exportToCSV, 
  checkDataQuality,
  convertToLegacyFormat 
} from '../utils/dataProcessor';

export default function CarAnalysisDashboard() {
  const [rawData, setRawData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [viewMode, setViewMode] = useState('overview');
  const [timeScale, setTimeScale] = useState('monthly');
  const [selectedGrades, setSelectedGrades] = useState([]);
  const [yearRange, setYearRange] = useState({ min: 2014, max: 2025 });
  const [mileageMax, setMileageMax] = useState(200000);
  const [showNormalizedGrades, setShowNormalizedGrades] = useState(true);
  const [fileUploaded, setFileUploaded] = useState(false);
  const [dataQuality, setDataQuality] = useState(null);
  const [lastUpdate, setLastUpdate] = useState(null);

  // サンプルデータ生成（既存CSVデータの構造に合わせる）
  const generateSampleData = () => {
    const grades = [
      'RC カーボンエクステリアパッケージ',
      'RC 5.0',
      'RC パフォーマンスパッケージ',
      'RC F 10th アニバーサリー',
      'RC ファイナル エディション',
      'RC エモーショナル ツーリング'
    ];
    
    const normalizedGrades = [
      'カーボンエクステリア',
      '5.0',
      'パフォーマンス',
      '10th アニバーサリー',
      'ファイナル エディション',
      'エモーショナル ツーリング'
    ];
    
    const years = [2014, 2015, 2016, 2018, 2019, 2020, 2021, 2022, 2023, 2024, 2025];
    const transmissions = ['フロアMTモード付8AT', 'フロア8AT'];
    
    const sampleData = [];
    for (let i = 0; i < 80; i++) {
      const gradeIndex = Math.floor(Math.random() * grades.length);
      const year = years[Math.floor(Math.random() * years.length)];
      const mileage = Math.random() * 150000;
      
      // より現実的な価格設定
      let basePrice = 300;
      if (normalizedGrades[gradeIndex].includes('エモーショナル') || normalizedGrades[gradeIndex].includes('ファイナル')) {
        basePrice = 1200;
      } else if (normalizedGrades[gradeIndex].includes('10th')) {
        basePrice = 900;
      } else if (normalizedGrades[gradeIndex].includes('パフォーマンス')) {
        basePrice = 800;
      } else if (normalizedGrades[gradeIndex].includes('カーボン')) {
        basePrice = 600;
      }
      
      const yearFactor = Math.max(0.5, 1 - ((2025 - year) * 0.05));
      const mileageFactor = Math.max(0.6, 1 - (mileage / 100000) * 0.2);
      const price = Math.round(basePrice * yearFactor * mileageFactor * (0.8 + Math.random() * 0.4) * 10) / 10;
      
      const month = Math.floor(Math.random() * 12) + 1;
      const day = Math.floor(Math.random() * 28) + 1;
      
      sampleData.push({
        車種名: 'F',
        モデル: '情報なし',
        グレード: grades[gradeIndex],
        正規グレード: normalizedGrades[gradeIndex],
        支払総額: `${price}万円`,
        年式: `${year}(${year >= 2019 ? 'R' + String(year - 2018).padStart(2, '0') : 'H' + (year - 1988)})`,
        走行距離: `${Math.round(mileage / 10000 * 10) / 10}万km`,
        修復歴: Math.random() > 0.85 ? 'あり' : 'なし',
        ミッション: transmissions[Math.floor(Math.random() * transmissions.length)],
        排気量: '5000CC',
        マッチング精度: 0.7 + Math.random() * 0.3,
        取得日時: `2024-${String(month).padStart(2, '0')}-${String(day).padStart(2, '0')}T12:45:11.583278`,
        ソースURL: 'https://www.carsensor.net/usedcar/bLE/s016/index.html'
      });
    }
    
    const processedData = processCarData(sampleData);
    setRawData(processedData);
    setFileUploaded(false);
    setLastUpdate(new Date().toLocaleString());
    
    // データ品質チェック
    const quality = checkDataQuality(processedData);
    setDataQuality(quality);
    
    // 初期フィルター設定
    const uniqueGrades = [...new Set(processedData.map(item => 
      showNormalizedGrades ? item.正規グレード : item.元グレード
    ))].filter(Boolean);
    setSelectedGrades(uniqueGrades);
  };

  // CSVファイルアップロード処理
  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (!file) return;

    setLoading(true);
    Papa.parse(file, {
      header: true,
      encoding: 'UTF-8',
      complete: (results) => {
        console.log('アップロードされたCSV:', results.data);
        const processedData = processCarData(results.data);
        setRawData(processedData);
        setFileUploaded(true);
        setLastUpdate(new Date().toLocaleString());
        
        // データ品質チェック
        const quality = checkDataQuality(processedData);
        setDataQuality(quality);
        
        // 初期フィルター設定
        const uniqueGrades = [...new Set(processedData.map(item => 
          showNormalizedGrades ? item.正規グレード : item.元グレード
        ))].filter(Boolean);
        setSelectedGrades(uniqueGrades);
        setLoading(false);
      },
      error: (error) => {
        console.error('CSV解析エラー:', error);
        setLoading(false);
        alert('CSVファイルの読み込みに失敗しました。');
      }
    });
  };

  // 初期データ読み込み
  useEffect(() => {
    generateSampleData();
  }, []);

  // フィルター適用後のデータ
  const filteredData = useMemo(() => {
    return rawData.filter(item => {
      const targetGrade = showNormalizedGrades ? item.正規グレード : item.元グレード;
      
      if (selectedGrades.length > 0 && !selectedGrades.includes(targetGrade)) return false;
      if (item.year < yearRange.min || item.year > yearRange.max) return false;
      if (item.mileage > mileageMax) return false;
      return true;
    });
  }, [rawData, selectedGrades, yearRange, mileageMax, showNormalizedGrades]);

  // 年式分布データ
  const yearDistributionData = useMemo(() => {
    const yearCounts = {};
    filteredData.forEach(item => {
      yearCounts[item.year] = (yearCounts[item.year] || 0) + 1;
    });
    
    return Object.entries(yearCounts)
      .map(([year, count]) => ({ year: parseInt(year), count }))
      .sort((a, b) => a.year - b.year);
  }, [filteredData]);

  // 修復歴統計
  const repairHistoryData = useMemo(() => {
    const stats = { あり: 0, なし: 0 };
    filteredData.forEach(item => {
      stats[item.修復歴] = (stats[item.修復歴] || 0) + 1;
    });
    
    return [
      { name: '修復歴なし', value: stats.なし, fill: '#10b981' },
      { name: '修復歴あり', value: stats.あり, fill: '#ef4444' }
    ];
  }, [filteredData]);

  // 利用可能なグレード一覧
  const availableGrades = useMemo(() => {
    return [...new Set(rawData.map(item => 
      showNormalizedGrades ? item.正規グレード : item.元グレード
    ))].filter(Boolean).sort();
  }, [rawData, showNormalizedGrades]);

  // グレード選択切り替え
  const handleGradeToggle = (grade) => {
    setSelectedGrades(prev => 
      prev.includes(grade) 
        ? prev.filter(g => g !== grade)
        : [...prev, grade]
    );
  };

  // 全選択/全解除
  const handleSelectAllGrades = () => {
    setSelectedGrades(selectedGrades.length === availableGrades.length ? [] : availableGrades);
  };

  // グレード表示切り替え
  const handleGradeTypeChange = (useNormalized) => {
    setShowNormalizedGrades(useNormalized);
    const uniqueGrades = [...new Set(rawData.map(item => 
      useNormalized ? item.正規グレード : item.元グレード
    ))].filter(Boolean);
    setSelectedGrades(uniqueGrades);
  };

  // データエクスポート
  const handleExportData = () => {
    const exportData = convertToLegacyFormat(filteredData);
    exportToCSV(exportData, `rc_f_analysis_${new Date().toISOString().split('T')[0]}.csv`);
  };

  if (loading) {
    return (
      <div style={{ 
        display: 'flex', 
        alignItems: 'center', 
        justifyContent: 'center', 
        height: '100vh', 
        backgroundColor: '#f9fafb' 
      }}>
        <div style={{ textAlign: 'center' }}>
          <div style={{ 
            width: '48px', 
            height: '48px', 
            border: '4px solid #e5e7eb',
            borderTop: '4px solid #3b82f6',
            borderRadius: '50%',
            animation: 'spin 1s linear infinite',
            margin: '0 auto 16px'
          }}></div>
          <p style={{ color: '#6b7280' }}>データを読み込み中...</p>
        </div>
      </div>
    );
  }

  return (
    <div style={{ minHeight: '100vh', backgroundColor: '#f9fafb' }}>
      {/* ヘッダー */}
      <div style={{ 
        backgroundColor: 'white', 
        boxShadow: '0 1px 3px 0 rgba(0, 0, 0, 0.1)', 
        borderBottom: '1px solid #e5e7eb' 
      }}>
        <div style={{ 
          maxWidth: '1280px', 
          margin: '0 auto', 
          padding: '0 16px' 
        }}>
          <div style={{ 
            display: 'flex', 
            justifyContent: 'space-between', 
            alignItems: 'center', 
            padding: '16px 0' 
          }}>
            <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
              <Car size={32} color="#2563eb" />
              <div>
                <h1 style={{ 
                  fontSize: '24px', 
                  fontWeight: 'bold', 
                  color: '#111827', 
                  margin: '0' 
                }}>
                  RC F 中古車分析ダッシュボード
                </h1>
                {lastUpdate && (
                  <p style={{ 
                    fontSize: '14px', 
                    color: '#6b7280', 
                    margin: '4px 0 0 0' 
                  }}>
                    最終更新: {lastUpdate}
                  </p>
                )}
              </div>
            </div>
            
            <div style={{ display: 'flex', alignItems: 'center', gap: '16px' }}>
              {/* データ品質インジケーター */}
              {dataQuality && (
                <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
                  {dataQuality.qualityScore >= 90 ? (
                    <CheckCircle size={20} color="#10b981" />
                  ) : (
                    <AlertCircle size={20} color="#f59e0b" />
                  )}
                  <span style={{ fontSize: '14px', color: '#6b7280' }}>
                    品質: {dataQuality.qualityScore}%
                  </span>
                </div>
              )}
              
              <button
                onClick={handleExportData}
                style={{
                  display: 'flex',
                  alignItems: 'center',
                  gap: '8px',
                  padding: '8px 12px',
                  backgroundColor: '#10b981',
                  color: 'white',
                  border: 'none',
                  borderRadius: '6px',
                  cursor: 'pointer',
                  fontSize: '14px'
                }}
              >
                <Download size={16} />
                エクスポート
              </button>
              
              {/* ファイルアップロード */}
              <div style={{ position: 'relative' }}>
                <input
                  type="file"
                  accept=".csv"
                  onChange={handleFileUpload}
                  style={{
                    position: 'absolute',
                    inset: '0',
                    width: '100%',
                    height: '100%',
                    opacity: '0',
                    cursor: 'pointer'
                  }}
                />
                <button style={{
                  display: 'flex',
                  alignItems: 'center',
                  gap: '8px',
                  padding: '8px 16px',
                  backgroundColor: '#3b82f6',
                  color: 'white',
                  border: 'none',
                  borderRadius: '6px',
                  cursor: 'pointer',
                  fontSize: '14px'
                }}>
                  <Upload size={16} />
                  CSVアップロード
                </button>
              </div>
              
              <span style={{ fontSize: '14px', color: '#6b7280' }}>
                表示中: <span style={{ fontWeight: '600', color: '#3b82f6' }}>{filteredData.length}</span>件 / 
                全<span style={{ fontWeight: '600' }}>{rawData.length}</span>件
              </span>
            </div>
          </div>
        </div>
      </div>

      {/* メインコンテンツ */}
      <div style={{ maxWidth: '1280px', margin: '0 auto', padding: '24px 16px' }}>
        <div style={{ display: 'grid', gridTemplateColumns: '1fr 3fr', gap: '24px' }}>
          
          {/* サイドバー - フィルター */}
          <div>
            <div style={{ 
              backgroundColor: 'white', 
              borderRadius: '8px', 
              boxShadow: '0 1px 3px 0 rgba(0, 0, 0, 0.1)', 
              border: '1px solid #e5e7eb', 
              padding: '24px',
              position: 'sticky',
              top: '24px'
            }}>
              <div style={{ display: 'flex', alignItems: 'center', gap: '8px', marginBottom: '16px' }}>
                <Filter size={20} color="#6b7280" />
                <h2 style={{ fontSize: '18px', fontWeight: '600', color: '#111827', margin: '0' }}>フィルター</h2>
              </div>

              {/* データ品質情報 */}
              {dataQuality && (
                <div style={{ 
                  marginBottom: '24px', 
                  padding: '12px', 
                  backgroundColor: '#f9fafb', 
                  borderRadius: '6px' 
                }}>
                  <h3 style={{ fontSize: '14px', fontWeight: '500', color: '#374751', marginBottom: '8px' }}>データ品質</h3>
                  <div style={{ fontSize: '12px', color: '#6b7280' }}>
                    <div style={{ marginBottom: '4px' }}>有効データ: {dataQuality.validCount}件</div>
                    <div>品質スコア: {dataQuality.qualityScore}%</div>
                  </div>
                </div>
              )}

              {/* グレード表示切り替え */}
              <div style={{ marginBottom: '24px' }}>
                <label style={{ 
                  display: 'block', 
                  fontSize: '14px', 
                  fontWeight: '500', 
                  color: '#374751', 
                  marginBottom: '8px' 
                }}>
                  グレード表示
                </label>
                <div style={{ display: 'flex', flexDirection: 'column', gap: '8px' }}>
                  <label style={{ display: 'flex', alignItems: 'center' }}>
                    <input
                      type="radio"
                      checked={showNormalizedGrades}
                      onChange={() => handleGradeTypeChange(true)}
                      style={{ marginRight: '8px', accentColor: '#3b82f6' }}
                    />
                    <span style={{ fontSize: '14px' }}>正規グレード</span>
                  </label>
                  <label style={{ display: 'flex', alignItems: 'center' }}>
                    <input
                      type="radio"
                      checked={!showNormalizedGrades}
                      onChange={() => handleGradeTypeChange(false)}
                      style={{ marginRight: '8px', accentColor: '#3b82f6' }}
                    />
                    <span style={{ fontSize: '14px' }}>元グレード</span>
                  </label>
                </div>
              </div>

              {/* グレード選択 */}
              <div style={{ marginBottom: '24px' }}>
                <div style={{ 
                  display: 'flex', 
                  justifyContent: 'space-between', 
                  alignItems: 'center', 
                  marginBottom: '8px' 
                }}>
                  <label style={{ 
                    fontSize: '14px', 
                    fontWeight: '500', 
                    color: '#374751' 
                  }}>
                    グレード選択
                  </label>
                  <button
                    onClick={handleSelectAllGrades}
                    style={{
                      fontSize: '12px',
                      color: '#3b82f6',
                      background: 'none',
                      border: 'none',
                      cursor: 'pointer',
                      textDecoration: 'underline'
                    }}
                  >
                    {selectedGrades.length === availableGrades.length ? '全解除' : '全選択'}
                  </button>
                </div>
                <div style={{ 
                  maxHeight: '200px', 
                  overflowY: 'auto', 
                  border: '1px solid #d1d5db', 
                  borderRadius: '6px', 
                  padding: '8px', 
                  backgroundColor: '#f9fafb' 
                }}>
                  {availableGrades.map(grade => (
                    <label key={grade} style={{ 
                      display: 'flex', 
                      alignItems: 'center', 
                      marginBottom: '4px', 
                      padding: '4px', 
                      borderRadius: '4px',
                      cursor: 'pointer'
                    }}>
                      <input
                        type="checkbox"
                        checked={selectedGrades.includes(grade)}
                        onChange={() => handleGradeToggle(grade)}
                        style={{ marginRight: '8px', accentColor: '#3b82f6' }}
                      />
                      <span style={{ 
                        fontSize: '14px', 
                        color: '#374751',
                        overflow: 'hidden',
                        textOverflow: 'ellipsis',
                        whiteSpace: 'nowrap'
                      }} title={grade}>
                        {grade}
                      </span>
                    </label>
                  ))}
                </div>
              </div>

              {/* 年式範囲 */}
              <div style={{ marginBottom: '24px' }}>
                <label style={{ 
                  display: 'block', 
                  fontSize: '14px', 
                  fontWeight: '500', 
                  color: '#374751', 
                  marginBottom: '8px' 
                }}>
                  年式範囲
                </label>
                <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
                  <input
                    type="number"
                    value={yearRange.min}
                    onChange={(e) => setYearRange(prev => ({ ...prev, min: parseInt(e.target.value) }))}
                    min="2014"
                    max="2025"
                    style={{
                      width: '80px',
                      border: '1px solid #d1d5db',
                      borderRadius: '6px',
                      padding: '4px 8px',
                      fontSize: '14px'
                    }}
                  />
                  <span style={{ color: '#6b7280' }}>〜</span>
                  <input
                    type="number"
                    value={yearRange.max}
                    onChange={(e) => setYearRange(prev => ({ ...prev, max: parseInt(e.target.value) }))}
                    min="2014"
                    max="2025"
                    style={{
                      width: '80px',
                      border: '1px solid #d1d5db',
                      borderRadius: '6px',
                      padding: '4px 8px',
                      fontSize: '14px'
                    }}
                  />
                </div>
              </div>

              {/* 走行距離 */}
              <div style={{ marginBottom: '24px' }}>
                <label style={{ 
                  display: 'block', 
                  fontSize: '14px', 
                  fontWeight: '500', 
                  color: '#374751', 
                  marginBottom: '8px' 
                }}>
                  走行距離上限: {Math.round(mileageMax / 10000 * 10) / 10}万km
                </label>
                <input
                  type="range"
                  min="0"
                  max="200000"
                  step="10000"
                  value={mileageMax}
                  onChange={(e) => setMileageMax(parseInt(e.target.value))}
                  style={{ width: '100%', accentColor: '#3b82f6' }}
                />
              </div>
            </div>
          </div>

          {/* メインコンテンツ */}
          <div style={{ display: 'flex', flexDirection: 'column', gap: '24px' }}>
            
            {/* 表示モード選択 */}
            <div style={{ 
              backgroundColor: 'white', 
              borderRadius: '8px', 
              boxShadow: '0 1px 3px 0 rgba(0, 0, 0, 0.1)', 
              border: '1px solid #e5e7eb', 
              padding: '16px' 
            }}>
              <div style={{ display: 'flex', flexWrap: 'wrap', gap: '8px' }}>
                {[
                  { key: 'overview', label: '概要', icon: BarChart3 },
                  { key: 'trend', label: '価格推移', icon: TrendingUp },
                  { key: 'grades', label: 'グレード分析', icon: BarChart3 },
                  { key: 'scatter', label: '走行距離vs価格', icon: MapPin }
                ].map(({ key, label, icon: Icon }) => (
                  <button
                    key={key}
                    onClick={() => setViewMode(key)}
                    style={{
                      display: 'flex',
                      alignItems: 'center',
                      gap: '8px',
                      padding: '8px 16px',
                      borderRadius: '6px',
                      border: 'none',
                      cursor: 'pointer',
                      fontSize: '14px',
                      backgroundColor: viewMode === key ? '#3b82f6' : '#f3f4f6',
                      color: viewMode === key ? 'white' : '#374751',
                      boxShadow: viewMode === key ? '0 1px 3px 0 rgba(0, 0, 0, 0.1)' : 'none'
                    }}
                  >
                    <Icon size={16} />
                    <span>{label}</span>
                  </button>
                ))}
              </div>
            </div>

            {/* 統計カード */}
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(4, 1fr)', gap: '16px' }}>
              <div style={{ 
                backgroundColor: 'white', 
                borderRadius: '8px', 
                boxShadow: '0 1px 3px 0 rgba(0, 0, 0, 0.1)', 
                border: '1px solid #e5e7eb', 
                padding: '24px' 
              }}>
                <div style={{ fontSize: '14px', color: '#6b7280', marginBottom: '4px' }}>平均価格</div>
                <div style={{ fontSize: '24px', fontWeight: 'bold', color: '#3b82f6' }}>
                  {filteredData.length > 0 
                    ? `${Math.round(filteredData.reduce((sum, item) => sum + item.price, 0) / filteredData.length * 10) / 10}万円`
                    : '-'
                  }
                </div>
              </div>
              <div style={{ 
                backgroundColor: 'white', 
                borderRadius: '8px', 
                boxShadow: '0 1px 3px 0 rgba(0, 0, 0, 0.1)', 
                border: '1px solid #e5e7eb', 
                padding: '24px' 
              }}>
                <div style={{ fontSize: '14px', color: '#6b7280', marginBottom: '4px' }}>グレード数</div>
                <div style={{ fontSize: '24px', fontWeight: 'bold', color: '#10b981' }}>
                  {availableGrades.length}種類
                </div>
              </div>
              <div style={{ 
                backgroundColor: 'white', 
                borderRadius: '8px', 
                boxShadow: '0 1px 3px 0 rgba(0, 0, 0, 0.1)', 
                border: '1px solid #e5e7eb', 
                padding: '24px' 
              }}>
                <div style={{ fontSize: '14px', color: '#6b7280', marginBottom: '4px' }}>平均年式</div>
                <div style={{ fontSize: '24px', fontWeight: 'bold', color: '#8b5cf6' }}>
                  {filteredData.length > 0 
                    ? `${Math.round(filteredData.reduce((sum, item) => sum + item.year, 0) / filteredData.length)}年`
                    : '-'
                  }
                </div>
              </div>
              <div style={{ 
                backgroundColor: 'white', 
                borderRadius: '8px', 
                boxShadow: '0 1px 3px 0 rgba(0, 0, 0, 0.1)', 
                border: '1px solid #e5e7eb', 
                padding: '24px' 
              }}>
                <div style={{ fontSize: '14px', color: '#6b7280', marginBottom: '4px' }}>平均走行距離</div>
                <div style={{ fontSize: '24px', fontWeight: 'bold', color: '#f59e0b' }}>
                  {filteredData.length > 0 
                    ? `${Math.round(filteredData.reduce((sum, item) => sum + item.mileage, 0) / filteredData.length / 10000 * 10) / 10}万km`
                    : '-'
                  }
                </div>
              </div>
            </div>

            {/* グラフエリア */}
            {viewMode === 'overview' && (
              <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '24px' }}>
                {/* 年式分布 */}
                <div style={{ 
                  backgroundColor: 'white', 
                  borderRadius: '8px', 
                  boxShadow: '0 1px 3px 0 rgba(0, 0, 0, 0.1)', 
                  border: '1px solid #e5e7eb', 
                  padding: '24px' 
                }}>
                  <h3 style={{ 
                    fontSize: '18px', 
                    fontWeight: '600', 
                    color: '#111827', 
                    marginBottom: '16px' 
                  }}>
                    年式分布
                  </h3>
                  <ResponsiveContainer width="100%" height={300}>
                    <BarChart data={yearDistributionData}>
                      <CartesianGrid strokeDasharray="3 3" stroke="#f0f0f0" />
                      <XAxis dataKey="year" stroke="#666" />
                      <YAxis stroke="#666" />
                      <Tooltip 
                        formatter={(value) => [`${value}台`, '台数']}
                        labelFormatter={(label) => `${label}年`}
                      />
                      <Bar dataKey="count" fill="#3b82f6" radius={[4, 4, 0, 0]} />
                    </BarChart>
                  </ResponsiveContainer>
                </div>

                {/* 修復歴統計 */}
                <div style={{ 
                  backgroundColor: 'white', 
                  borderRadius: '8px', 
                  boxShadow: '0 1px 3px 0 rgba(0, 0, 0, 0.1)', 
                  border: '1px solid #e5e7eb', 
                  padding: '24px' 
                }}>
                  <h3 style={{ 
                    fontSize: '18px', 
                    fontWeight: '600', 
                    color: '#111827', 
                    marginBottom: '16px' 
                  }}>
                    修復歴統計
                  </h3>
                  <ResponsiveContainer width="100%" height={300}>
                    <PieChart>
                      <Pie
                        data={repairHistoryData}
                        cx="50%"
                        cy="50%"
                        outerRadius={100}
                        dataKey="value"
                        label={({ name, value, percent }) => 
                          `${name}: ${value}台 (${(percent * 100).toFixed(1)}%)`
                        }
                      >
                        {repairHistoryData.map((entry, index) => (
                          <Cell key={`cell-${index}`} fill={entry.fill} />
                        ))}
                      </Pie>
                      <Tooltip />
                    </PieChart>
                  </ResponsiveContainer>
                </div>
              </div>
            )}

            {/* 価格推移 */}
            {viewMode === 'trend' && (
              <div style={{ 
                backgroundColor: 'white', 
                borderRadius: '8px', 
                boxShadow: '0 1px 3px 0 rgba(0, 0, 0, 0.1)', 
                border: '1px solid #e5e7eb', 
                padding: '24px' 
              }}>
                <div style={{ 
                  display: 'flex', 
                  justifyContent: 'space-between', 
                  alignItems: 'center', 
                  marginBottom: '16px' 
                }}>
                  <h3 style={{ 
                    fontSize: '18px', 
                    fontWeight: '600', 
                    color: '#111827' 
                  }}>
                    価格推移
                  </h3>
                  <div style={{ display: 'flex', gap: '8px' }}>
                    {['monthly', 'yearly'].map(scale => (
                      <button
                        key={scale}
                        onClick={() => setTimeScale(scale)}
                        style={{
                          padding: '4px 12px',
                          borderRadius: '6px',
                          fontSize: '14px',
                          border: 'none',
                          cursor: 'pointer',
                          backgroundColor: timeScale === scale ? '#3b82f6' : '#f3f4f6',
                          color: timeScale === scale ? 'white' : '#374751'
                        }}
                      >
                        {scale === 'monthly' ? '月次' : '年次'}
                      </button>
                    ))}
                  </div>
                </div>
                <ResponsiveContainer width="100%" height={400}>
                  <LineChart data={[]}>
                    <CartesianGrid strokeDasharray="3 3" stroke="#f0f0f0" />
                    <XAxis dataKey="date" stroke="#666" />
                    <YAxis label={{ value: '価格 (万円)', angle: -90, position: 'insideLeft' }} stroke="#666" />
                    <Tooltip 
                      formatter={(value, name) => [`${value}万円`, name]}
                      labelFormatter={(label) => `期間: ${label}`}
                    />
                    <Legend />
                    <Line 
                      type="monotone" 
                      dataKey="avgPrice" 
                      stroke="#3b82f6" 
                      strokeWidth={3}
                      name="平均価格"
                      dot={{ fill: '#3b82f6', strokeWidth: 2, r: 4 }}
                    />
                  </LineChart>
                </ResponsiveContainer>
                <div style={{ marginTop: '16px', fontSize: '14px', color: '#6b7280' }}>
                  <p>💡 データが蓄積されると価格の推移がグラフで表示されます</p>
                </div>
              </div>
            )}

            {/* グレード分析 */}
            {viewMode === 'grades' && (
              <div style={{ 
                backgroundColor: 'white', 
                borderRadius: '8px', 
                boxShadow: '0 1px 3px 0 rgba(0, 0, 0, 0.1)', 
                border: '1px solid #e5e7eb', 
                padding: '24px' 
              }}>
                <h3 style={{ 
                  fontSize: '18px', 
                  fontWeight: '600', 
                  color: '#111827', 
                  marginBottom: '16px' 
                }}>
                  グレード別価格分析
                </h3>
                <ResponsiveContainer width="100%" height={400}>
                  <BarChart data={[]} margin={{ bottom: 100 }}>
                    <CartesianGrid strokeDasharray="3 3" stroke="#f0f0f0" />
                    <XAxis 
                      dataKey="grade" 
                      angle={-45} 
                      textAnchor="end" 
                      height={100} 
                      stroke="#666"
                      interval={0}
                    />
                    <YAxis label={{ value: '価格 (万円)', angle: -90, position: 'insideLeft' }} stroke="#666" />
                    <Tooltip 
                      formatter={(value, name) => [`${value}万円`, name]}
                    />
                    <Legend />
                    <Bar dataKey="avgPrice" fill="#3b82f6" name="平均価格" radius={[4, 4, 0, 0]} />
                    <Bar dataKey="minPrice" fill="#10b981" name="最低価格" radius={[4, 4, 0, 0]} />
                    <Bar dataKey="maxPrice" fill="#ef4444" name="最高価格" radius={[4, 4, 0, 0]} />
                  </BarChart>
                </ResponsiveContainer>
                
                {/* グレード別統計テーブル */}
                <div style={{ marginTop: '24px' }}>
                  <h4 style={{ 
                    fontSize: '16px', 
                    fontWeight: '600', 
                    color: '#111827', 
                    marginBottom: '12px' 
                  }}>
                    グレード別詳細データ
                  </h4>
                  <div style={{ overflowX: 'auto' }}>
                    <table style={{ 
                      width: '100%', 
                      borderCollapse: 'collapse', 
                      border: '1px solid #d1d5db' 
                    }}>
                      <thead>
                        <tr style={{ backgroundColor: '#f9fafb' }}>
                          <th style={{ 
                            border: '1px solid #d1d5db', 
                            padding: '8px 16px', 
                            textAlign: 'left',
                            fontSize: '14px',
                            fontWeight: '600'
                          }}>
                            グレード
                          </th>
                          <th style={{ 
                            border: '1px solid #d1d5db', 
                            padding: '8px 16px', 
                            textAlign: 'left',
                            fontSize: '14px',
                            fontWeight: '600'
                          }}>
                            台数
                          </th>
                          <th style={{ 
                            border: '1px solid #d1d5db', 
                            padding: '8px 16px', 
                            textAlign: 'left',
                            fontSize: '14px',
                            fontWeight: '600'
                          }}>
                            平均価格
                          </th>
                          <th style={{ 
                            border: '1px solid #d1d5db', 
                            padding: '8px 16px', 
                            textAlign: 'left',
                            fontSize: '14px',
                            fontWeight: '600'
                          }}>
                            価格範囲
                          </th>
                        </tr>
                      </thead>
                      <tbody>
                        {availableGrades.slice(0, 5).map((grade, index) => {
                          const gradeData = filteredData.filter(item => 
                            (showNormalizedGrades ? item.正規グレード : item.元グレード) === grade
                          );
                          const avgPrice = gradeData.length > 0 
                            ? Math.round(gradeData.reduce((sum, item) => sum + item.price, 0) / gradeData.length * 10) / 10
                            : 0;
                          const minPrice = gradeData.length > 0 ? Math.min(...gradeData.map(item => item.price)) : 0;
                          const maxPrice = gradeData.length > 0 ? Math.max(...gradeData.map(item => item.price)) : 0;
                          
                          return (
                            <tr key={index} style={{ 
                              backgroundColor: index % 2 === 0 ? 'white' : '#f9fafb' 
                            }}>
                              <td style={{ 
                                border: '1px solid #d1d5db', 
                                padding: '8px 16px',
                                fontSize: '14px'
                              }}>
                                {grade}
                              </td>
                              <td style={{ 
                                border: '1px solid #d1d5db', 
                                padding: '8px 16px',
                                fontSize: '14px'
                              }}>
                                {gradeData.length}台
                              </td>
                              <td style={{ 
                                border: '1px solid #d1d5db', 
                                padding: '8px 16px',
                                fontSize: '14px',
                                fontWeight: '600',
                                color: '#3b82f6'
                              }}>
                                {avgPrice}万円
                              </td>
                              <td style={{ 
                                border: '1px solid #d1d5db', 
                                padding: '8px 16px',
                                fontSize: '14px'
                              }}>
                                {minPrice}〜{maxPrice}万円
                              </td>
                            </tr>
                          );
                        })}
                      </tbody>
                    </table>
                  </div>
                </div>
              </div>
            )}

            {/* 走行距離vs価格 */}
            {viewMode === 'scatter' && (
              <div style={{ 
                backgroundColor: 'white', 
                borderRadius: '8px', 
                boxShadow: '0 1px 3px 0 rgba(0, 0, 0, 0.1)', 
                border: '1px solid #e5e7eb', 
                padding: '24px' 
              }}>
                <h3 style={{ 
                  fontSize: '18px', 
                  fontWeight: '600', 
                  color: '#111827', 
                  marginBottom: '16px' 
                }}>
                  走行距離 vs 価格
                </h3>
                <ResponsiveContainer width="100%" height={400}>
                  <ScatterChart data={filteredData}>
                    <CartesianGrid strokeDasharray="3 3" stroke="#f0f0f0" />
                    <XAxis 
                      type="number" 
                      dataKey="mileage" 
                      name="走行距離"
                      label={{ value: '走行距離 (km)', position: 'insideBottom', offset: -5 }}
                      stroke="#666"
                    />
                    <YAxis 
                      type="number" 
                      dataKey="price" 
                      name="価格"
                      label={{ value: '価格 (万円)', angle: -90, position: 'insideLeft' }}
                      stroke="#666"
                    />
                    <Tooltip 
                      cursor={{ strokeDasharray: '3 3' }}
                      content={({ active, payload }) => {
                        if (active && payload && payload.length > 0) {
                          const data = payload[0].payload;
                          return (
                            <div style={{
                              backgroundColor: 'white',
                              border: '1px solid #d1d5db',
                              borderRadius: '6px',
                              padding: '12px',
                              boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)'
                            }}>
                              <p style={{ fontSize: '14px', margin: '0 0 4px 0' }}>
                                <strong>価格:</strong> {data.price}万円
                              </p>
                              <p style={{ fontSize: '14px', margin: '0 0 4px 0' }}>
                                <strong>走行距離:</strong> {Math.round(data.mileage / 10000 * 10) / 10}万km
                              </p>
                              <p style={{ fontSize: '14px', margin: '0 0 4px 0' }}>
                                <strong>年式:</strong> {data.year}年
                              </p>
                              <p style={{ fontSize: '14px', margin: '0 0 4px 0' }}>
                                <strong>グレード:</strong> {data.正規グレード}
                              </p>
                              <p style={{ fontSize: '14px', margin: '0' }}>
                                <strong>修復歴:</strong> {data.修復歴}
                              </p>
                            </div>
                          );
                        }
                        return null;
                      }}
                    />
                    <Scatter dataKey="price" fill="#3b82f6" />
                  </ScatterChart>
                </ResponsiveContainer>
                <div style={{ marginTop: '16px', fontSize: '14px', color: '#6b7280' }}>
                  <p>💡 <strong>ヒント:</strong> 各点は1台の車両を表しています。左下ほど高コスパ、右上ほど価格が高い傾向があります。</p>
                </div>
              </div>
            )}

          </div>
        </div>
      </div>
    </div>
  );
}
