import React, { useState, useEffect, useMemo, useRef } from 'react';
import { LineChart, Line, BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, PieChart, Pie, Cell, ScatterChart, Scatter } from 'recharts';
import { Car, Calendar, TrendingUp, DollarSign, Activity, Settings, Download, Upload, Info, X, ExternalLink, AlertCircle, ChevronDown, ChevronUp, Gauge, Wrench, History, Package, ArrowUpDown, Filter, RotateCcw, Eye, EyeOff, Check } from 'lucide-react';

// グレード名統一設定
const CAR_GRADES_CONFIG = {
  "RC F": {
    grades: [
      "RC F",
      "RC F Carbon Exterior Package",
      "RC F Performance Package",
      "RC F Track Edition",
      "RC F 10th Anniversary",
      "ベース"
    ],
    special_patterns: {
      "カーボンエクステリアパッケージ": "RC F Carbon Exterior Package",
      "パフォーマンスパッケージ": "RC F Performance Package",
      "トラックエディション": "RC F Track Edition",
      "10周年": "RC F 10th Anniversary"
    }
  }
};

// グレード名を統一する関数
const normalizeGrade = (grade, carType) => {
  if (!grade || !carType) return grade;
  
  const config = CAR_GRADES_CONFIG[carType];
  if (!config) return grade;
  
  // 完全一致チェック
  if (config.grades.includes(grade)) {
    return grade;
  }
  
  // 特殊パターンチェック
  for (const [pattern, normalized] of Object.entries(config.special_patterns)) {
    if (grade.includes(pattern)) {
      return normalized;
    }
  }
  
  // 部分一致チェック
  for (const normalizedGrade of config.grades) {
    if (grade.includes(normalizedGrade) || normalizedGrade.includes(grade)) {
      return normalizedGrade;
    }
  }
  
  // マッチしない場合は元のグレード名を返す
  return grade;
};

// データ処理関数
const parsePrice = (priceStr) => {
  if (!priceStr) return null;
  const cleaned = priceStr.toString().replace(/[万円,]/g, '');
  const price = parseFloat(cleaned);
  return isNaN(price) ? null : price;
};

const parseYear = (yearStr) => {
  if (!yearStr) return null;
  const match = yearStr.toString().match(/\d{4}/);
  return match ? parseInt(match[0]) : null;
};

const parseMileage = (mileageStr) => {
  if (!mileageStr) return null;
  const cleaned = mileageStr.toString().replace(/[kmKM,]/g, '');
  const mileage = parseFloat(cleaned);
  if (isNaN(mileage)) return null;
  return mileageStr.toString().includes('万') ? mileage * 10000 : mileage;
};

const processCarData = (data, carType) => {
  return data.map(item => {
    const price = parsePrice(item['価格']);
    const year = parseYear(item['年式']);
    const mileage = parseMileage(item['走行距離']);
    
    // グレード名の統一
    const originalGrade = item['グレード'] || 'Unknown';
    const normalizedGrade = normalizeGrade(originalGrade, carType);
    
    // スクレイピング取得日の処理
    let scrapeDate = null;
    if (item['スクレイピング取得日']) {
      const dateStr = item['スクレイピング取得日'];
      const dateParts = dateStr.match(/(\d{4})年(\d{1,2})月(\d{1,2})日/);
      if (dateParts) {
        scrapeDate = new Date(
          parseInt(dateParts[1]),
          parseInt(dateParts[2]) - 1,
          parseInt(dateParts[3])
        );
      }
    }
    
    return {
      ...item,
      price,
      year,
      mileage,
      scrapeDate,
      元グレード: originalGrade,
      正規グレード: normalizedGrade,
      グレード: normalizedGrade, // デフォルトは正規グレード
      修復歴: item['修復歴'] || 'Unknown',
      ミッション: item['ミッション'] || 'Unknown',
      url: item['URL'] || '#'
    };
  }).filter(item => item.price !== null && item.year !== null);
};

// データ品質チェック関数
const checkDataQuality = (data) => {
  if (!data || data.length === 0) return { score: 0, issues: ['No data available'] };
  
  const issues = [];
  let validCount = 0;
  const requiredFields = ['価格', '年式', '走行距離', 'グレード', '修復歴'];
  
  data.forEach(item => {
    let hasAllFields = true;
    requiredFields.forEach(field => {
      if (!item[field] || item[field] === 'Unknown') {
        hasAllFields = false;
      }
    });
    if (hasAllFields) validCount++;
  });
  
  const score = Math.round((validCount / data.length) * 100);
  
  if (score < 50) issues.push('Many missing values');
  if (score < 80) issues.push('Some data quality issues');
  
  return { score, issues };
};

// 統合レンジスライダーコンポーネント
const RangeSlider = ({ min, max, value, onChange, step = 1, label, unit = '' }) => {
  const [localValue, setLocalValue] = useState(value);
  const [isDragging, setIsDragging] = useState(false);
  
  useEffect(() => {
    setLocalValue(value);
  }, [value]);
  
  const handleMouseDown = (index) => {
    setIsDragging(index);
  };
  
  const handleMouseUp = () => {
    setIsDragging(false);
  };
  
  const handleMouseMove = (e) => {
    if (isDragging === false) return;
    
    const rect = e.currentTarget.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const percentage = Math.max(0, Math.min(1, x / rect.width));
    const newValue = min + (max - min) * percentage;
    
    const newRange = [...localValue];
    newRange[isDragging] = Math.round(newValue / step) * step;
    
    if (isDragging === 0 && newRange[0] > newRange[1]) {
      newRange[0] = newRange[1];
    } else if (isDragging === 1 && newRange[1] < newRange[0]) {
      newRange[1] = newRange[0];
    }
    
    setLocalValue(newRange);
    onChange(newRange);
  };
  
  useEffect(() => {
    if (isDragging !== false) {
      document.addEventListener('mouseup', handleMouseUp);
      return () => document.removeEventListener('mouseup', handleMouseUp);
    }
  }, [isDragging]);
  
  const leftPercentage = ((localValue[0] - min) / (max - min)) * 100;
  const rightPercentage = ((localValue[1] - min) / (max - min)) * 100;
  
  return (
    <div className="w-full">
      <div className="flex justify-between items-center mb-2">
        <span className="text-sm font-medium text-gray-700">{label}</span>
        <span className="text-sm text-gray-600">
          {localValue[0]}{unit} - {localValue[1]}{unit}
        </span>
      </div>
      <div 
        className="relative h-6 bg-gray-200 rounded-full cursor-pointer"
        onMouseMove={handleMouseMove}
      >
        <div 
          className="absolute h-full bg-blue-500 rounded-full"
          style={{
            left: `${leftPercentage}%`,
            width: `${rightPercentage - leftPercentage}%`
          }}
        />
        <div
          className="absolute w-6 h-6 bg-white border-2 border-blue-500 rounded-full shadow-lg cursor-grab active:cursor-grabbing transform -translate-x-1/2"
          style={{ left: `${leftPercentage}%` }}
          onMouseDown={() => handleMouseDown(0)}
        />
        <div
          className="absolute w-6 h-6 bg-white border-2 border-blue-500 rounded-full shadow-lg cursor-grab active:cursor-grabbing transform -translate-x-1/2"
          style={{ left: `${rightPercentage}%` }}
          onMouseDown={() => handleMouseDown(1)}
        />
      </div>
    </div>
  );
};

// CSVエクスポート関数
const exportToCSV = (data, filename) => {
  const headers = ['価格', '年式', '走行距離', 'グレード', '元グレード', '修復歴', 'ミッション', 'URL'];
  const csvContent = [
    headers.join(','),
    ...data.map(row => 
      headers.map(header => {
        let value = row[header];
        if (header === '価格' && row.price) value = `${row.price}万円`;
        if (header === '走行距離' && row.mileage) value = `${row.mileage}km`;
        if (typeof value === 'string' && value.includes(',')) {
          value = `"${value}"`;
        }
        return value || '';
      }).join(',')
    )
  ].join('\n');
  
  const blob = new Blob(['\ufeff' + csvContent], { type: 'text/csv;charset=utf-8;' });
  const link = document.createElement('a');
  link.href = URL.createObjectURL(blob);
  link.download = filename;
  link.click();
};

const CarAnalysisDashboard = () => {
  const [rawData, setRawData] = useState([]);
  const [carTypeData, setCarTypeData] = useState({});
  const [availableFiles, setAvailableFiles] = useState({});
  const [selectedCarType, setSelectedCarType] = useState('RC F');
  const [selectedFile, setSelectedFile] = useState('');
  const [loading, setLoading] = useState(true);
  const [carTypeLoading, setCarTypeLoading] = useState(false);
  const [uploadedData, setUploadedData] = useState(null);
  const [viewMode, setViewMode] = useState('overview');
  const [showCarDetailModal, setShowCarDetailModal] = useState(false);
  const [selectedCar, setSelectedCar] = useState(null);
  const fileInputRef = useRef(null);
  
  // フィルター関連の状態
  const [selectedGrades, setSelectedGrades] = useState([]);
  const [yearRange, setYearRange] = useState({ min: 2014, max: 2025 });
  const [priceRange, setPriceRange] = useState([0, 2000]);
  const [mileageMax, setMileageMax] = useState(200000);
  const [transmissionFilters, setTransmissionFilters] = useState({
    'AT': true,
    'CVT': true,
    'MT': true,
    'その他': true
  });
  const [repairHistoryFilter, setRepairHistoryFilter] = useState('all');
  const [showNormalizedGrades, setShowNormalizedGrades] = useState(true);
  
  // 価格推移の表示設定
  const [trendBasedOn, setTrendBasedOn] = useState('scrapeDate');
  const [trendInterval, setTrendInterval] = useState('monthly');
  const [useLatestDataset, setUseLatestDataset] = useState(false);
  
  // 初期データ読み込み
  useEffect(() => {
    const loadMetadata = async () => {
      try {
        const response = await fetch('/data/metadata.json');
        const metadata = await response.json();
        setAvailableFiles(metadata);
        
        // RC Fのデフォルトファイルを設定（ディレクトリ名が"F"の場合の対応）
        const rcfFiles = metadata['RC F'] || metadata['F'] || [];
        if (rcfFiles.length > 0) {
          setSelectedFile(rcfFiles[0]);
        }
      } catch (error) {
        console.error('Error loading metadata:', error);
      }
    };
    
    loadMetadata();
  }, []);
  
  // 車種変更時のファイル自動選択
  useEffect(() => {
    const files = availableFiles[selectedCarType] || [];
    if (files.length > 0 && !files.includes(selectedFile)) {
      setSelectedFile(files[0]);
    }
  }, [selectedCarType, availableFiles]);
  
  // データ読み込み
  useEffect(() => {
    if (selectedCarType && selectedFile) {
      loadCarData();
    }
  }, [selectedCarType, selectedFile]);
  
  const loadCarData = async () => {
    setCarTypeLoading(true);
    try {
      // ディレクトリ名の対応（RC F -> F）
      const directoryName = selectedCarType === 'RC F' ? 'F' : selectedCarType;
      const response = await fetch(`/data/${directoryName}/${selectedFile}`);
      const data = await response.json();
      const processed = processCarData(data, selectedCarType);
      setCarTypeData({ [selectedCarType]: processed });
      setRawData(processed);
      
      // グレードの初期選択
      const grades = [...new Set(processed.map(car => car[showNormalizedGrades ? '正規グレード' : '元グレード']))];
      setSelectedGrades(grades);
    } catch (error) {
      console.error('Error loading car data:', error);
    } finally {
      setCarTypeLoading(false);
      setLoading(false);
    }
  };
  
  // グレード表示切り替え時の処理
  useEffect(() => {
    const currentData = carTypeData[selectedCarType] || [];
    if (currentData.length > 0) {
      // グレードフィールドを更新
      const updatedData = currentData.map(car => ({
        ...car,
        グレード: showNormalizedGrades ? car.正規グレード : car.元グレード
      }));
      setCarTypeData({ [selectedCarType]: updatedData });
      setRawData(updatedData);
      
      // 選択中のグレードを更新
      const grades = [...new Set(updatedData.map(car => car.グレード))];
      setSelectedGrades(grades);
    }
  }, [showNormalizedGrades]);
  
  // フィルタリング処理
  const filteredData = useMemo(() => {
    const currentData = uploadedData || rawData;
    
    return currentData.filter(car => {
      const grade = car[showNormalizedGrades ? '正規グレード' : '元グレード'];
      if (selectedGrades.length > 0 && !selectedGrades.includes(grade)) return false;
      if (car.year < yearRange.min || car.year > yearRange.max) return false;
      if (car.price < priceRange[0] || car.price > priceRange[1]) return false;
      if (car.mileage > mileageMax) return false;
      
      const transmission = car['ミッション'] || 'その他';
      const transmissionCategory = ['AT', 'CVT', 'MT'].includes(transmission) ? transmission : 'その他';
      if (!transmissionFilters[transmissionCategory]) return false;
      
      if (repairHistoryFilter === 'なし' && car['修復歴'] !== 'なし') return false;
      if (repairHistoryFilter === 'あり' && car['修復歴'] !== 'あり') return false;
      
      return true;
    });
  }, [rawData, uploadedData, selectedGrades, yearRange, priceRange, mileageMax, transmissionFilters, repairHistoryFilter, showNormalizedGrades]);
  
  // 統計データの計算
  const statistics = useMemo(() => {
    if (filteredData.length === 0) {
      return {
        avgPrice: 0,
        minPrice: 0,
        maxPrice: 0,
        avgYear: 0,
        totalCars: 0,
        avgMileage: 0,
        noRepairRatio: 0
      };
    }
    
    const prices = filteredData.map(car => car.price);
    const years = filteredData.map(car => car.year);
    const mileages = filteredData.map(car => car.mileage).filter(m => m !== null);
    const noRepairCount = filteredData.filter(car => car['修復歴'] === 'なし').length;
    
    return {
      avgPrice: Math.round(prices.reduce((a, b) => a + b, 0) / prices.length),
      minPrice: Math.min(...prices),
      maxPrice: Math.max(...prices),
      avgYear: Math.round(years.reduce((a, b) => a + b, 0) / years.length),
      totalCars: filteredData.length,
      avgMileage: mileages.length > 0 ? Math.round(mileages.reduce((a, b) => a + b, 0) / mileages.length) : 0,
      noRepairRatio: Math.round((noRepairCount / filteredData.length) * 100)
    };
  }, [filteredData]);
  
  // 価格推移データの生成
  const trendData = useMemo(() => {
    if (filteredData.length === 0) return [];
    
    let dataToGroup = useLatestDataset ? filteredData : rawData;
    
    if (trendBasedOn === 'year') {
      // 年式基準
      const yearData = {};
      dataToGroup.forEach(car => {
        const year = car.year;
        if (year) {
          if (!yearData[year]) {
            yearData[year] = { prices: [], count: 0 };
          }
          yearData[year].prices.push(car.price);
          yearData[year].count++;
        }
      });
      
      return Object.entries(yearData)
        .map(([year, data]) => ({
          name: year,
          平均価格: Math.round(data.prices.reduce((a, b) => a + b, 0) / data.prices.length),
          中央値: Math.round(data.prices.sort((a, b) => a - b)[Math.floor(data.prices.length / 2)]),
          最高価格: Math.max(...data.prices),
          最低価格: Math.min(...data.prices),
          台数: data.count
        }))
        .sort((a, b) => parseInt(a.name) - parseInt(b.name));
    } else {
      // 取得日基準
      const dateData = {};
      dataToGroup.forEach(car => {
        if (car.scrapeDate) {
          let key;
          if (trendInterval === 'daily') {
            key = car.scrapeDate.toISOString().split('T')[0];
          } else if (trendInterval === 'monthly') {
            key = `${car.scrapeDate.getFullYear()}-${String(car.scrapeDate.getMonth() + 1).padStart(2, '0')}`;
          } else {
            key = car.scrapeDate.getFullYear().toString();
          }
          
          if (!dateData[key]) {
            dateData[key] = { prices: [], count: 0 };
          }
          dateData[key].prices.push(car.price);
          dateData[key].count++;
        }
      });
      
      return Object.entries(dateData)
        .map(([date, data]) => ({
          name: date,
          平均価格: Math.round(data.prices.reduce((a, b) => a + b, 0) / data.prices.length),
          中央値: Math.round(data.prices.sort((a, b) => a - b)[Math.floor(data.prices.length / 2)]),
          最高価格: Math.max(...data.prices),
          最低価格: Math.min(...data.prices),
          台数: data.count
        }))
        .sort((a, b) => a.name.localeCompare(b.name));
    }
  }, [filteredData, rawData, trendBasedOn, trendInterval, useLatestDataset]);
  
  // グレード分析データ
  const gradeAnalysisData = useMemo(() => {
    const gradeData = {};
    const gradeField = showNormalizedGrades ? '正規グレード' : '元グレード';
    
    filteredData.forEach(car => {
      const grade = car[gradeField];
      if (!gradeData[grade]) {
        gradeData[grade] = { prices: [], count: 0 };
      }
      gradeData[grade].prices.push(car.price);
      gradeData[grade].count++;
    });
    
    return Object.entries(gradeData)
      .map(([grade, data]) => {
        const sortedPrices = data.prices.sort((a, b) => a - b);
        return {
          grade,
          平均価格: Math.round(data.prices.reduce((a, b) => a + b, 0) / data.prices.length),
          中央値: Math.round(sortedPrices[Math.floor(sortedPrices.length / 2)]),
          最低価格: Math.min(...data.prices),
          最高価格: Math.max(...data.prices),
          台数: data.count
        };
      })
      .sort((a, b) => b.平均価格 - a.平均価格);
  }, [filteredData, showNormalizedGrades]);
  
  // 年式分布データ
  const yearDistributionData = useMemo(() => {
    const yearCounts = {};
    const dataToAnalyze = useLatestDataset ? filteredData : rawData;
    
    dataToAnalyze.forEach(car => {
      yearCounts[car.year] = (yearCounts[car.year] || 0) + 1;
    });
    
    return Object.entries(yearCounts)
      .map(([year, count]) => ({ year: parseInt(year), count }))
      .sort((a, b) => a.year - b.year);
  }, [filteredData, rawData, useLatestDataset]);
  
  // 修復歴統計データ
  const repairHistoryData = useMemo(() => {
    const dataToAnalyze = useLatestDataset ? filteredData : rawData;
    const counts = { 'なし': 0, 'あり': 0, '不明': 0 };
    
    dataToAnalyze.forEach(car => {
      const history = car['修復歴'];
      if (history === 'なし') counts['なし']++;
      else if (history === 'あり') counts['あり']++;
      else counts['不明']++;
    });
    
    return Object.entries(counts).map(([name, value]) => ({ name, value }));
  }, [filteredData, rawData, useLatestDataset]);
  
  // 利用可能なグレード
  const availableGrades = useMemo(() => {
    const gradeField = showNormalizedGrades ? '正規グレード' : '元グレード';
    return [...new Set(rawData.map(car => car[gradeField]))].sort();
  }, [rawData, showNormalizedGrades]);
  
  // データ品質
  const dataQuality = useMemo(() => checkDataQuality(filteredData), [filteredData]);
  
  // CSVファイルアップロード処理
  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    
    const reader = new FileReader();
    reader.onload = (event) => {
      try {
        const text = event.target.result;
        const lines = text.split('\n');
        const headers = lines[0].split(',');
        
        const data = lines.slice(1)
          .filter(line => line.trim())
          .map(line => {
            const values = line.split(',');
            const item = {};
            headers.forEach((header, index) => {
              item[header.trim()] = values[index]?.trim() || '';
            });
            return item;
          });
        
        const processed = processCarData(data, selectedCarType);
        setUploadedData(processed);
        setRawData(processed);
        
        // グレードリセット
        const grades = [...new Set(processed.map(car => car[showNormalizedGrades ? '正規グレード' : '元グレード']))];
        setSelectedGrades(grades);
      } catch (error) {
        console.error('Error parsing CSV:', error);
        alert('CSVファイルの読み込みに失敗しました。');
      }
    };
    reader.readAsText(file);
  };
  
  const resetFilters = () => {
    setSelectedGrades(availableGrades);
    setYearRange({ min: 2014, max: 2025 });
    setPriceRange([0, 2000]);
    setMileageMax(200000);
    setTransmissionFilters({
      'AT': true,
      'CVT': true,
      'MT': true,
      'その他': true
    });
    setRepairHistoryFilter('all');
  };
  
  const COLORS = ['#3B82F6', '#10B981', '#F59E0B', '#EF4444', '#8B5CF6', '#EC4899'];
  
  // 短縮グレード名の生成
  const truncateGradeName = (name, maxLength = 15) => {
    if (!name || name.length <= maxLength) return name;
    return name.substring(0, maxLength) + '...';
  };
  
  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen bg-gray-50">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto"></div>
          <p className="mt-4 text-gray-600">データを読み込んでいます...</p>
        </div>
      </div>
    );
  }
  
  return (
    <div className="min-h-screen bg-gray-50">
      <div className="bg-white shadow">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between py-4">
            <div className="flex items-center space-x-4">
              <Car className="h-8 w-8 text-blue-600" />
              <h1 className="text-2xl font-bold text-gray-900">中古車分析ダッシュボード</h1>
            </div>
            <div className="flex items-center space-x-4">
              <select
                value={selectedCarType}
                onChange={(e) => setSelectedCarType(e.target.value)}
                className="px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                disabled={carTypeLoading}
              >
                {Object.keys(availableFiles).map(carType => (
                  <option key={carType} value={carType}>{carType}</option>
                ))}
              </select>
              
              <select
                value={selectedFile}
                onChange={(e) => setSelectedFile(e.target.value)}
                className="px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                disabled={carTypeLoading}
              >
                {(availableFiles[selectedCarType] || []).map(file => (
                  <option key={file} value={file}>{file}</option>
                ))}
              </select>
              
              <button
                onClick={() => fileInputRef.current?.click()}
                className="px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 transition-colors"
              >
                <Upload className="h-4 w-4 inline mr-2" />
                CSV取込
              </button>
              
              <button
                onClick={() => exportToCSV(filteredData, `${selectedCarType}_analysis_${new Date().toISOString().split('T')[0]}.csv`)}
                className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
                disabled={filteredData.length === 0}
              >
                <Download className="h-4 w-4 inline mr-2" />
                CSV出力
              </button>
            </div>
          </div>
        </div>
      </div>
      
      <input
        ref={fileInputRef}
        type="file"
        accept=".csv"
        onChange={handleFileUpload}
        className="hidden"
      />
      
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
        <div className="flex gap-6">
          {/* サイドバー */}
          <div className="w-80 bg-white rounded-lg shadow p-6 h-fit sticky top-6">
            <div className="flex items-center justify-between mb-6">
              <h2 className="text-lg font-semibold text-gray-900">フィルター</h2>
              <button
                onClick={resetFilters}
                className="text-sm text-blue-600 hover:text-blue-800 flex items-center"
              >
                <RotateCcw className="h-4 w-4 mr-1" />
                リセット
              </button>
            </div>
            
            <div className="space-y-6">
              {/* グレード表示切り替え */}
              <div>
                <div className="flex items-center justify-between mb-3">
                  <label className="text-sm font-medium text-gray-700">グレード表示</label>
                  <button
                    onClick={() => setShowNormalizedGrades(!showNormalizedGrades)}
                    className="flex items-center text-sm text-blue-600 hover:text-blue-800"
                  >
                    {showNormalizedGrades ? <Eye className="h-4 w-4 mr-1" /> : <EyeOff className="h-4 w-4 mr-1" />}
                    {showNormalizedGrades ? '正規グレード' : '元グレード'}
                  </button>
                </div>
              </div>
              
              {/* グレード選択 */}
              <div>
                <div className="flex items-center justify-between mb-3">
                  <label className="text-sm font-medium text-gray-700">グレード</label>
                  <div className="space-x-2">
                    <button
                      onClick={() => setSelectedGrades(availableGrades)}
                      className="text-xs text-blue-600 hover:text-blue-800"
                    >
                      全選択
                    </button>
                    <button
                      onClick={() => setSelectedGrades([])}
                      className="text-xs text-blue-600 hover:text-blue-800"
                    >
                      全解除
                    </button>
                  </div>
                </div>
                <div className="space-y-2 max-h-48 overflow-y-auto">
                  {availableGrades.map(grade => (
                    <label key={grade} className="flex items-center text-sm">
                      <input
                        type="checkbox"
                        checked={selectedGrades.includes(grade)}
                        onChange={(e) => {
                          if (e.target.checked) {
                            setSelectedGrades([...selectedGrades, grade]);
                          } else {
                            setSelectedGrades(selectedGrades.filter(g => g !== grade));
                          }
                        }}
                        className="mr-2 text-blue-600 rounded focus:ring-blue-500"
                      />
                      <span className="truncate" title={grade}>{grade}</span>
                    </label>
                  ))}
                </div>
              </div>
              
              {/* 年式範囲 */}
              <div>
                <label className="text-sm font-medium text-gray-700">年式範囲</label>
                <div className="flex items-center space-x-4 mt-2">
                  <input
                    type="number"
                    value={yearRange.min}
                    onChange={(e) => setYearRange({ ...yearRange, min: parseInt(e.target.value) || 2014 })}
                    className="w-20 px-2 py-1 border border-gray-300 rounded"
                    min="2014"
                    max="2025"
                  />
                  <span className="text-gray-500">〜</span>
                  <input
                    type="number"
                    value={yearRange.max}
                    onChange={(e) => setYearRange({ ...yearRange, max: parseInt(e.target.value) || 2025 })}
                    className="w-20 px-2 py-1 border border-gray-300 rounded"
                    min="2014"
                    max="2025"
                  />
                </div>
              </div>
              
              {/* 価格範囲 */}
              <div>
                <RangeSlider
                  min={0}
                  max={2000}
                  value={priceRange}
                  onChange={setPriceRange}
                  step={10}
                  label="価格範囲"
                  unit="万円"
                />
              </div>
              
              {/* 走行距離 */}
              <div>
                <label className="text-sm font-medium text-gray-700">
                  走行距離上限: {(mileageMax / 10000).toFixed(1)}万km
                </label>
                <input
                  type="range"
                  min="0"
                  max="200000"
                  step="5000"
                  value={mileageMax}
                  onChange={(e) => setMileageMax(parseInt(e.target.value))}
                  className="w-full mt-2"
                />
              </div>
              
              {/* ミッション */}
              <div>
                <label className="text-sm font-medium text-gray-700 mb-2 block">ミッション</label>
                <div className="space-y-2">
                  {Object.entries(transmissionFilters).map(([type, checked]) => (
                    <label key={type} className="flex items-center text-sm">
                      <input
                        type="checkbox"
                        checked={checked}
                        onChange={(e) => setTransmissionFilters({
                          ...transmissionFilters,
                          [type]: e.target.checked
                        })}
                        className="mr-2"
                      />
                      {type}
                    </label>
                  ))}
                </div>
              </div>
              
              {/* 修復歴 */}
              <div>
                <label className="text-sm font-medium text-gray-700 mb-2 block">修復歴</label>
                <div className="space-y-2">
                  {[
                    { value: 'all', label: 'すべて' },
                    { value: 'なし', label: 'なし' },
                    { value: 'あり', label: 'あり' }
                  ].map(option => (
                    <label key={option.value} className="flex items-center text-sm">
                      <input
                        type="radio"
                        name="repairHistory"
                        value={option.value}
                        checked={repairHistoryFilter === option.value}
                        onChange={(e) => setRepairHistoryFilter(e.target.value)}
                        className="mr-2"
                      />
                      {option.label}
                    </label>
                  ))}
                </div>
              </div>
              
              {/* フィルター適用件数 */}
              <div className="pt-4 border-t">
                <p className="text-sm text-gray-600">
                  {filteredData.length}件 / {rawData.length}件
                </p>
              </div>
            </div>
          </div>
          
          {/* メインコンテンツ */}
          <div className="flex-1">
            {/* ビューモード切り替え */}
            <div className="bg-white rounded-lg shadow p-4 mb-6">
              <div className="flex space-x-4">
                {[
                  { id: 'overview', label: '概要', icon: Info },
                  { id: 'trend', label: '価格推移', icon: TrendingUp },
                  { id: 'grade', label: 'グレード分析', icon: Package },
                  { id: 'scatter', label: '走行距離vs価格', icon: Activity }
                ].map(mode => (
                  <button
                    key={mode.id}
                    onClick={() => setViewMode(mode.id)}
                    className={`flex items-center px-4 py-2 rounded-lg transition-colors ${
                      viewMode === mode.id
                        ? 'bg-blue-600 text-white'
                        : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                    }`}
                  >
                    <mode.icon className="h-4 w-4 mr-2" />
                    {mode.label}
                  </button>
                ))}
              </div>
            </div>
            
            {carTypeLoading ? (
              <div className="bg-white rounded-lg shadow p-8">
                <div className="flex items-center justify-center">
                  <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
                  <p className="ml-4 text-gray-600">データを読み込んでいます...</p>
                </div>
              </div>
            ) : (
              <>
                {/* 概要モード */}
                {viewMode === 'overview' && (
                  <div className="space-y-6">
                    {/* 統計カード */}
                    <div className="grid grid-cols-2 lg:grid-cols-5 gap-4">
                      <div className="bg-white rounded-lg shadow p-4">
                        <div className="flex items-center justify-between">
                          <div>
                            <p className="text-sm text-gray-600">平均価格</p>
                            <p className="text-2xl font-bold text-gray-900">{statistics.avgPrice}万円</p>
                          </div>
                          <DollarSign className="h-8 w-8 text-green-500" />
                        </div>
                      </div>
                      
                      <div className="bg-white rounded-lg shadow p-4">
                        <div className="flex items-center justify-between">
                          <div>
                            <p className="text-sm text-gray-600">価格帯</p>
                            <p className="text-2xl font-bold text-gray-900">
                              {statistics.minPrice}-{statistics.maxPrice}万
                            </p>
                          </div>
                          <TrendingUp className="h-8 w-8 text-blue-500" />
                        </div>
                      </div>
                      
                      <div className="bg-white rounded-lg shadow p-4">
                        <div className="flex items-center justify-between">
                          <div>
                            <p className="text-sm text-gray-600">グレード数</p>
                            <p className="text-2xl font-bold text-gray-900">{availableGrades.length}</p>
                          </div>
                          <Package className="h-8 w-8 text-purple-500" />
                        </div>
                      </div>
                      
                      <div className="bg-white rounded-lg shadow p-4">
                        <div className="flex items-center justify-between">
                          <div>
                            <p className="text-sm text-gray-600">平均年式</p>
                            <p className="text-2xl font-bold text-gray-900">{statistics.avgYear}年</p>
                          </div>
                          <Calendar className="h-8 w-8 text-orange-500" />
                        </div>
                      </div>
                      
                      <div className="bg-white rounded-lg shadow p-4">
                        <div className="flex items-center justify-between">
                          <div>
                            <p className="text-sm text-gray-600">修復歴なし</p>
                            <p className="text-2xl font-bold text-gray-900">{statistics.noRepairRatio}%</p>
                          </div>
                          <Check className="h-8 w-8 text-green-500" />
                        </div>
                      </div>
                    </div>
                    
                    {/* グラフ */}
                    <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                      {/* 年式分布 */}
                      <div className="bg-white rounded-lg shadow p-6">
                        <div className="flex items-center justify-between mb-4">
                          <h3 className="text-lg font-semibold text-gray-900">年式分布</h3>
                          <div className="flex items-center space-x-2">
                            <label className="text-sm text-gray-600">データソース:</label>
                            <select
                              value={useLatestDataset ? 'filtered' : 'all'}
                              onChange={(e) => setUseLatestDataset(e.target.value === 'filtered')}
                              className="text-sm px-2 py-1 border border-gray-300 rounded"
                            >
                              <option value="all">全期間</option>
                              <option value="filtered">最新データセット</option>
                            </select>
                          </div>
                        </div>
                        <ResponsiveContainer width="100%" height={300}>
                          <BarChart data={yearDistributionData}>
                            <CartesianGrid strokeDasharray="3 3" />
                            <XAxis dataKey="year" />
                            <YAxis />
                            <Tooltip />
                            <Bar dataKey="count" fill="#3B82F6" />
                          </BarChart>
                        </ResponsiveContainer>
                      </div>
                      
                      {/* 修復歴統計 */}
                      <div className="bg-white rounded-lg shadow p-6">
                        <h3 className="text-lg font-semibold text-gray-900 mb-4">修復歴統計</h3>
                        <ResponsiveContainer width="100%" height={300}>
                          <PieChart>
                            <Pie
                              data={repairHistoryData}
                              dataKey="value"
                              nameKey="name"
                              cx="50%"
                              cy="50%"
                              outerRadius={80}
                              label={({ name, percent }) => `${name} ${(percent * 100).toFixed(0)}%`}
                            >
                              {repairHistoryData.map((entry, index) => (
                                <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                              ))}
                            </Pie>
                            <Tooltip />
                          </PieChart>
                        </ResponsiveContainer>
                        <div className="mt-4">
                          <table className="w-full text-sm">
                            <thead>
                              <tr className="border-b">
                                <th className="text-left py-2">修復歴</th>
                                <th className="text-right py-2">台数</th>
                                <th className="text-right py-2">割合</th>
                              </tr>
                            </thead>
                            <tbody>
                              {repairHistoryData.map((item, index) => (
                                <tr key={item.name} className="border-b">
                                  <td className="py-2 flex items-center">
                                    <div
                                      className="w-3 h-3 rounded-full mr-2"
                                      style={{ backgroundColor: COLORS[index % COLORS.length] }}
                                    />
                                    {item.name}
                                  </td>
                                  <td className="text-right py-2">{item.value}</td>
                                  <td className="text-right py-2">
                                    {Math.round((item.value / (useLatestDataset ? filteredData.length : rawData.length)) * 100)}%
                                  </td>
                                </tr>
                              ))}
                            </tbody>
                          </table>
                        </div>
                      </div>
                    </div>
                    
                    {/* データ品質インジケーター */}
                    <div className="bg-white rounded-lg shadow p-6">
                      <h3 className="text-lg font-semibold text-gray-900 mb-4">データ品質</h3>
                      <div className="flex items-center space-x-4">
                        <div className="flex-1">
                          <div className="bg-gray-200 rounded-full h-4 relative">
                            <div
                              className={`h-full rounded-full transition-all ${
                                dataQuality.score >= 80 ? 'bg-green-500' :
                                dataQuality.score >= 50 ? 'bg-yellow-500' : 'bg-red-500'
                              }`}
                              style={{ width: `${dataQuality.score}%` }}
                            />
                          </div>
                        </div>
                        <span className="text-lg font-semibold">{dataQuality.score}%</span>
                      </div>
                      {dataQuality.issues.length > 0 && (
                        <div className="mt-2">
                          {dataQuality.issues.map((issue, index) => (
                            <p key={index} className="text-sm text-yellow-600 flex items-center">
                              <AlertCircle className="h-4 w-4 mr-1" />
                              {issue}
                            </p>
                          ))}
                        </div>
                      )}
                    </div>
                  </div>
                )}
                
                {/* 価格推移モード */}
                {viewMode === 'trend' && (
                  <div className="bg-white rounded-lg shadow p-6">
                    <div className="flex items-center justify-between mb-6">
                      <h3 className="text-lg font-semibold text-gray-900">価格推移</h3>
                      <div className="flex items-center space-x-4">
                        <div className="flex items-center space-x-2">
                          <label className="text-sm text-gray-600">基準:</label>
                          <select
                            value={trendBasedOn}
                            onChange={(e) => setTrendBasedOn(e.target.value)}
                            className="text-sm px-2 py-1 border border-gray-300 rounded"
                          >
                            <option value="scrapeDate">取得日</option>
                            <option value="year">年式</option>
                          </select>
                        </div>
                        {trendBasedOn === 'scrapeDate' && (
                          <div className="flex items-center space-x-2">
                            <label className="text-sm text-gray-600">期間:</label>
                            <select
                              value={trendInterval}
                              onChange={(e) => setTrendInterval(e.target.value)}
                              className="text-sm px-2 py-1 border border-gray-300 rounded"
                            >
                              <option value="daily">日次</option>
                              <option value="monthly">月次</option>
                              <option value="yearly">年次</option>
                            </select>
                          </div>
                        )}
                        <div className="flex items-center space-x-2">
                          <label className="text-sm text-gray-600">データ:</label>
                          <select
                            value={useLatestDataset ? 'filtered' : 'all'}
                            onChange={(e) => setUseLatestDataset(e.target.value === 'filtered')}
                            className="text-sm px-2 py-1 border border-gray-300 rounded"
                          >
                            <option value="all">全期間</option>
                            <option value="filtered">フィルター適用</option>
                          </select>
                        </div>
                      </div>
                    </div>
                    <ResponsiveContainer width="100%" height={400}>
                      <LineChart data={trendData}>
                        <CartesianGrid strokeDasharray="3 3" />
                        <XAxis 
                          dataKey="name"
                          angle={-45}
                          textAnchor="end"
                          height={80}
                        />
                        <YAxis />
                        <Tooltip />
                        <Legend />
                        <Line type="monotone" dataKey="平均価格" stroke="#3B82F6" strokeWidth={2} />
                        <Line type="monotone" dataKey="中央値" stroke="#10B981" strokeWidth={2} />
                        <Line type="monotone" dataKey="最高価格" stroke="#EF4444" strokeWidth={1} strokeDasharray="5 5" />
                        <Line type="monotone" dataKey="最低価格" stroke="#F59E0B" strokeWidth={1} strokeDasharray="5 5" />
                      </LineChart>
                    </ResponsiveContainer>
                  </div>
                )}
                
                {/* グレード分析モード */}
                {viewMode === 'grade' && (
                  <div className="space-y-6">
                    <div className="bg-white rounded-lg shadow p-6">
                      <h3 className="text-lg font-semibold text-gray-900 mb-4">グレード別価格比較</h3>
                      <ResponsiveContainer width="100%" height={400}>
                        <BarChart data={gradeAnalysisData} margin={{ left: 50, right: 50, bottom: 100 }}>
                          <CartesianGrid strokeDasharray="3 3" />
                          <XAxis 
                            dataKey="grade" 
                            angle={-45} 
                            textAnchor="end" 
                            height={100}
                            interval={0}
                            tick={{ fontSize: 12 }}
                          />
                          <YAxis />
                          <Tooltip 
                            content={({ active, payload }) => {
                              if (active && payload && payload.length) {
                                const data = payload[0].payload;
                                return (
                                  <div className="bg-white p-3 border border-gray-200 rounded shadow-lg">
                                    <p className="font-semibold text-sm mb-2">{data.grade}</p>
                                    <p className="text-sm">平均価格: {data.平均価格}万円</p>
                                    <p className="text-sm">中央値: {data.中央値}万円</p>
                                    <p className="text-sm">台数: {data.台数}台</p>
                                  </div>
                                );
                              }
                              return null;
                            }}
                          />
                          <Legend />
                          <Bar dataKey="平均価格" fill="#3B82F6" />
                          <Bar dataKey="中央値" fill="#10B981" />
                        </BarChart>
                      </ResponsiveContainer>
                    </div>
                    
                    <div className="bg-white rounded-lg shadow p-6">
                      <h3 className="text-lg font-semibold text-gray-900 mb-4">グレード詳細統計</h3>
                      <div className="overflow-x-auto">
                        <table className="w-full text-sm">
                          <thead>
                            <tr className="border-b">
                              <th className="text-left py-2">グレード</th>
                              <th className="text-right py-2">台数</th>
                              <th className="text-right py-2">平均価格</th>
                              <th className="text-right py-2">中央値</th>
                              <th className="text-right py-2">最低価格</th>
                              <th className="text-right py-2">最高価格</th>
                            </tr>
                          </thead>
                          <tbody>
                            {gradeAnalysisData.map(grade => (
                              <tr key={grade.grade} className="border-b hover:bg-gray-50">
                                <td className="py-2" title={grade.grade}>{grade.grade}</td>
                                <td className="text-right py-2">{grade.台数}</td>
                                <td className="text-right py-2">{grade.平均価格}万円</td>
                                <td className="text-right py-2">{grade.中央値}万円</td>
                                <td className="text-right py-2">{grade.最低価格}万円</td>
                                <td className="text-right py-2">{grade.最高価格}万円</td>
                              </tr>
                            ))}
                          </tbody>
                        </table>
                      </div>
                    </div>
                  </div>
                )}
                
                {/* 走行距離vs価格モード */}
                {viewMode === 'scatter' && (
                  <div className="bg-white rounded-lg shadow p-6">
                    <h3 className="text-lg font-semibold text-gray-900 mb-4">走行距離 vs 価格</h3>
                    <ResponsiveContainer width="100%" height={500}>
                      <ScatterChart>
                        <CartesianGrid strokeDasharray="3 3" />
                        <XAxis 
                          dataKey="mileage" 
                          name="走行距離" 
                          unit="km"
                          domain={[0, 'dataMax']}
                          tickFormatter={(value) => `${(value / 10000).toFixed(1)}万`}
                        />
                        <YAxis 
                          dataKey="price" 
                          name="価格" 
                          unit="万円"
                          domain={[0, 'dataMax']}
                        />
                        <Tooltip 
                          cursor={{ strokeDasharray: '3 3' }}
                          content={({ active, payload }) => {
                            if (active && payload && payload.length) {
                              const data = payload[0].payload;
                              return (
                                <div className="bg-white p-3 border border-gray-200 rounded shadow-lg">
                                  <p className="font-semibold text-sm mb-2">車両詳細</p>
                                  <p className="text-sm">価格: {data.price}万円</p>
                                  <p className="text-sm">走行距離: {(data.mileage / 10000).toFixed(1)}万km</p>
                                  <p className="text-sm">年式: {data.year}年</p>
                                  <p className="text-sm">グレード: {data.グレード}</p>
                                  <p className="text-sm">修復歴: {data.修復歴}</p>
                                  <button
                                    onClick={() => {
                                      setSelectedCar(data);
                                      setShowCarDetailModal(true);
                                    }}
                                    className="mt-2 text-blue-600 hover:text-blue-800 text-sm underline"
                                  >
                                    詳細を見る
                                  </button>
                                </div>
                              );
                            }
                            return null;
                          }}
                        />
                        <Scatter 
                          name="車両" 
                          data={filteredData} 
                          fill="#3B82F6"
                          onClick={(data) => {
                            setSelectedCar(data);
                            setShowCarDetailModal(true);
                          }}
                          style={{ cursor: 'pointer' }}
                        />
                      </ScatterChart>
                    </ResponsiveContainer>
                  </div>
                )}
              </>
            )}
          </div>
        </div>
      </div>
      
      {/* 車両詳細モーダル */}
      {showCarDetailModal && selectedCar && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-lg max-w-2xl w-full max-h-[90vh] overflow-y-auto">
            <div className="p-6">
              <div className="flex items-center justify-between mb-4">
                <h3 className="text-xl font-semibold">車両詳細情報</h3>
                <button
                  onClick={() => setShowCarDetailModal(false)}
                  className="text-gray-400 hover:text-gray-600"
                >
                  <X className="h-6 w-6" />
                </button>
              </div>
              
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <p className="text-sm text-gray-600">価格</p>
                  <p className="text-lg font-semibold">{selectedCar.price}万円</p>
                </div>
                <div>
                  <p className="text-sm text-gray-600">年式</p>
                  <p className="text-lg font-semibold">{selectedCar.year}年</p>
                </div>
                <div>
                  <p className="text-sm text-gray-600">走行距離</p>
                  <p className="text-lg font-semibold">
                    {selectedCar.mileage ? `${(selectedCar.mileage / 10000).toFixed(1)}万km` : '不明'}
                  </p>
                </div>
                <div>
                  <p className="text-sm text-gray-600">グレード</p>
                  <p className="text-lg font-semibold">{selectedCar.グレード}</p>
                </div>
                <div>
                  <p className="text-sm text-gray-600">修復歴</p>
                  <p className="text-lg font-semibold">{selectedCar.修復歴}</p>
                </div>
                <div>
                  <p className="text-sm text-gray-600">ミッション</p>
                  <p className="text-lg font-semibold">{selectedCar.ミッション}</p>
                </div>
              </div>
              
              {selectedCar.mileage && selectedCar.year && (
                <div className="mt-6 p-4 bg-gray-50 rounded-lg">
                  <h4 className="font-semibold mb-2">コスパ指標</h4>
                  <div className="grid grid-cols-2 gap-4 text-sm">
                    <div>
                      <p className="text-gray-600">年間走行距離</p>
                      <p className="font-semibold">
                        {Math.round(selectedCar.mileage / (2025 - selectedCar.year))}km/年
                      </p>
                    </div>
                    <div>
                      <p className="text-gray-600">車両年数</p>
                      <p className="font-semibold">{2025 - selectedCar.year}年</p>
                    </div>
                  </div>
                </div>
              )}
              
              {selectedCar.url && selectedCar.url !== '#' && (
                <div className="mt-6">
                  <a
                    href={selectedCar.url}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="inline-flex items-center px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
                  >
                    <ExternalLink className="h-4 w-4 mr-2" />
                    カーセンサーで詳細を見る
                  </a>
                </div>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default CarAnalysisDashboard;
